import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import NavBar from "./NavBar";
import Spinner from "./Spinner";
import Alert from "./Alert";
import LedgerReport from "./LedgerReport";

const Ledger = () => {
  const navigate = useNavigate();

  // State management
  const [showReport, setShowReport] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [alertType, setAlertType] = useState(null);
  const [alertMessage, setAlertMessage] = useState("");
  const [ledgerItems, setLedgerItems] = useState(null);
  const [partner, setPartner] = useState(null);
  const [partners, setPartners] = useState([]);
  const [formData, setFormData] = useState({
    partnerId: "",
    startDate: "",
    endDate: "",
  });

  const { partnerId, startDate, endDate } = formData;

  // Fetch partners on component mount
  useEffect(() => {
    const fetchPartners = async () => {
      try {
        const response = await axios.get(`/partners`);
        if (response.status === 200) {
          setPartners(response.data);
        } else {
          setAlertType("danger");
          setAlertMessage("Failed to fetch partners.");
        }
      } catch (error) {
        setAlertType("danger");
        setAlertMessage(`Error fetching partners: ${error.message}`);
      } finally {
        setIsLoading(false);
      }
    };

    fetchPartners();
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setAlertMessage(null);

    try {
      const response = await axios.get("/ledger", {
        params: {
          partnerId,
          startDate,
          endDate,
        },
      });

      if (response.status === 200 && response.data.ledgerItems.length > 0) {
        setPartner(response.data.partner);
        setLedgerItems(response.data.ledgerItems);
        setShowReport(true);
      } else {
        setAlertType("warning");
        setAlertMessage("No ledger data found for the given criteria.");
        setShowReport(false);
      }
    } catch (error) {
      setAlertType("danger");
      setAlertMessage(`Failed to retrieve ledger: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle cancel action
  const handleCancel = () => {
    navigate("/");
  };

  // Conditional Rendering
  return isLoading ? (
    <Spinner />
  ) : showReport ? (
    <LedgerReport
      partner={partner}
      ledgerItems={ledgerItems}
      startDate={startDate}
      endDate={endDate}
    />
  ) : (
    <>
      <NavBar />
      <div className="container mt-4">
        <div className="card shadow-lg border-0">
          {/* Header */}
          <div className="card-header bg-gradient-primary text-white">
            <h3 className="text-center mb-0">Generate Ledger Report</h3>
          </div>

          {/* Form Section */}
          <div className="card-body">
            {alertMessage && <Alert type={alertType} message={alertMessage} />}
            <form onSubmit={handleSubmit}>
              <div className="row">
                {/* Partner Selection */}
                <div className="col-md-4 mb-3">
                  <label htmlFor="partnerId" className="form-label">
                    Select Partner
                  </label>
                  <select
                    className="form-select"
                    id="partnerId"
                    name="partnerId"
                    value={partnerId}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Choose...</option>
                    {partners.map((partner) => (
                      <option key={partner._id} value={partner._id}>
                        {partner.firstName} {partner.lastName}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Start Date */}
                <div className="col-md-4 mb-3">
                  <label htmlFor="startDate" className="form-label">
                    Start Date
                  </label>
                  <input
                    type="date"
                    className="form-control"
                    id="startDate"
                    name="startDate"
                    value={startDate}
                    onChange={handleChange}
                    required
                  />
                </div>

                {/* End Date */}
                <div className="col-md-4 mb-3">
                  <label htmlFor="endDate" className="form-label">
                    End Date
                  </label>
                  <input
                    type="date"
                    className="form-control"
                    id="endDate"
                    name="endDate"
                    value={endDate}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              {/* Buttons */}
              <div className="text-center mt-4">
                <button type="submit" className="btn btn-success me-3">
                  <i className="fa fa-check"></i> Generate Report
                </button>
                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={handleCancel}
                >
                  <i className="fa fa-times"></i> Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

export default Ledger;
