import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";

const InvoiceReport = ({ invoiceType }) => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const invoiceId = queryParams.get("id");

  const [invoiceDetails, setInvoiceDetails] = useState(null);

  useEffect(() => {
    const fetchInvoiceDetails = async () => {
      try {
        const response = await axios.get(`/invoices/${invoiceId}`);
        setInvoiceDetails(response.data);
      } catch (error) {
        console.error("Failed to fetch invoice details:", error);
      }
    };

    if (invoiceId) {
      fetchInvoiceDetails();
    }
  }, [invoiceId]);

  if (!invoiceDetails) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mt-4">
      <h2>{invoiceType === "sales" ? "Sales" : "Purchase"} Invoice Report</h2>
      <p><strong>Invoice No:</strong> {invoiceDetails.invoiceNo}</p>
      <p><strong>Invoice Date:</strong> {invoiceDetails.invoiceDate}</p>
      <p><strong>Customer/Vendor:</strong> {invoiceDetails.partnerName}</p>
      <p><strong>Total:</strong> ${invoiceDetails.total}</p>
    </div>
  );
};

export default InvoiceReport;
