import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const LedgerReport = ({ partner, ledgerItems, startDate, endDate }) => {
  const navigate = useNavigate();

  const [reportData, setReportData] = useState({
    openingBalance: 0,
    closingBalance: 0,
    totalDebit: 0,
    totalCredit: 0,
    itemsWithBalance: [],
  });

  // Calculate balances and totals when the component mounts or dependencies change
  useEffect(() => {
    if (!partner || !ledgerItems) return;

    let openingBalance = partner.openingBalance || 0;
    let totalDebit = 0;
    let totalCredit = 0;
    let closingBalance = openingBalance;

    // Calculate balances after each transaction
    const itemsWithBalance = ledgerItems.map((item) => {
      if (item.type === "debit") {
        totalDebit += item.amount;
        closingBalance -= item.amount;
      } else if (item.type === "credit") {
        totalCredit += item.amount;
        closingBalance += item.amount;
      }

      return {
        ...item,
        balanceAfter: closingBalance,
      };
    });

    // Update report data
    setReportData({
      openingBalance,
      closingBalance,
      totalDebit,
      totalCredit,
      itemsWithBalance,
    });
  }, [partner, ledgerItems]);

  const handleBack = () => {
    navigate("/ledger"); // Redirect back to the ledger generation page
  };

  const { openingBalance, closingBalance, totalDebit, totalCredit, itemsWithBalance } = reportData;

  return (
    <div className="container mt-4">
      <div className="card shadow-lg border-0 col-md-10 offset-md-1">
        <div className="card-header bg-gradient-primary text-white">
          <h3 className="text-center mb-0">Ledger Report</h3>
        </div>
        <div className="card-body">
          {/* Partner Information */}
          <div className="mb-4">
            <h5>Partner Details</h5>
            <p>
              <strong>Name:</strong> {partner.firstName} {partner.lastName}
              <br />
              <strong>Status:</strong> {partner.status}
              <br />
              <strong>Contact:</strong> {partner.contactNo}
              <br />
              <strong>Address:</strong> {partner.address || "N/A"}
            </p>
          </div>

          {/* Report Duration */}
          <div className="mb-4">
            <h5>Report Duration</h5>
            <p>
              <strong>From:</strong> {startDate}
              <br />
              <strong>To:</strong> {endDate}
            </p>
          </div>

          {/* Opening Balance */}
          <div className="mb-4">
            <h5>Opening Balance</h5>
            <p>
              <strong>Amount:</strong> ${openingBalance.toLocaleString()}
            </p>
          </div>

          {/* Ledger Items Table */}
          <div className="table-responsive">
            <table className="table table-striped table-hover">
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Description</th>
                  <th>Type</th>
                  <th>Amount</th>
                  <th>Balance After</th>
                </tr>
              </thead>
              <tbody>
                {itemsWithBalance.length > 0 ? (
                  itemsWithBalance.map((item, index) => (
                    <tr key={index}>
                      <td>{item.date}</td>
                      <td>{item.description}</td>
                      <td>{item.type}</td>
                      <td>
                        {item.type === "debit" ? "-" : "+"}${item.amount.toLocaleString()}
                      </td>
                      <td>${item.balanceAfter.toLocaleString()}</td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="5" className="text-center">
                      No ledger entries available for the selected period.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>

          {/* Totals */}
          <div className="row mt-4">
            <div className="col-md-4">
              <h5>Total Debit</h5>
              <p>${totalDebit.toLocaleString()}</p>
            </div>
            <div className="col-md-4">
              <h5>Total Credit</h5>
              <p>${totalCredit.toLocaleString()}</p>
            </div>
            <div className="col-md-4">
              <h5>Closing Balance</h5>
              <p>${closingBalance.toLocaleString()}</p>
            </div>
          </div>

          {/* Back Button */}
          <div className="text-center mt-4">
            <button className="btn btn-primary" onClick={handleBack}>
              <i className="fa fa-arrow-left"></i> Back
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LedgerReport;
