import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Alert from "./Alert";

const AddInvoice = ({ invoiceType }) => {
  const initialState = {
    type: invoiceType,
    invoiceDate: new Date().toISOString().split("T")[0],
    dueDate: new Date().toISOString().split("T")[0],
    invoiceNo: "",
    partnerId: "",
    creditTerm: 0,
    reference: "",
    invoiceItems: [],
  };

  const [invoiceData, setInvoiceData] = useState(initialState);
  const [partners, setPartners] = useState([]);
  const [alert, setAlert] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const partnerType = invoiceType === "sales" ? "customers" : "vendors";
        const partnersResponse = await fetch(`/partners?type=${partnerType}`);
        const partnersData = await partnersResponse.json();

        if (Array.isArray(partnersData) && partnersData.length > 0) {
          setPartners(partnersData);
        } else {
          setAlert({ type: "warning", message: "No partners found." });
        }

        const invoicesResponse = await fetch(`/invoices?type=${invoiceType}`);
        const invoicesData = await invoicesResponse.json();
        const invoiceCount = invoicesData.length + 1;
        const prefix = invoiceType === "sales" ? "SI-" : "PI-";
        const invoiceNo = `${prefix}${String(invoiceCount).padStart(4, "0")}`;
        setInvoiceData((prev) => ({ ...prev, invoiceNo }));
      } catch (error) {
        setAlert({ type: "error", message: "Error fetching initial data." });
      }
    };

    fetchInitialData();
  }, [invoiceType]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoiceData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("/invoices", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(invoiceData),
      });

      if (response.ok) {
        setAlert({ type: "success", message: "Invoice added successfully!" });
        setInvoiceData(initialState);
      } else {
        const errorData = await response.json();
        setAlert({ type: "error", message: errorData.message || "Failed to add invoice." });
      }
    } catch (error) {
      setAlert({ type: "error", message: "An error occurred while adding the invoice." });
    }
  };

  const handleCancel = () => {
    navigate(`/invoices/${invoiceType}`);
  };

  return (
    <div className="container mt-5">
      {alert && <Alert type={alert.type} message={alert.message} className="mb-4" />}
      <div className="card shadow-lg border-0">
        <div className="card-header bg-gradient-primary text-white">
          <h3 className="text-center mb-0">Add {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice</h3>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="accordion" id="invoiceFormAccordion">
              <div className="accordion-item">
                <h2 className="accordion-header" id="basicInfoHeading">
                  <button
                    className="accordion-button"
                    type="button"
                    data-bs-toggle="collapse"
                    data-bs-target="#basicInfo"
                    aria-expanded="true"
                    aria-controls="basicInfo"
                  >
                    Basic Information
                  </button>
                </h2>
                <div
                  id="basicInfo"
                  className="accordion-collapse collapse show"
                  aria-labelledby="basicInfoHeading"
                >
                  <div className="accordion-body">
                    <div className="row">
                      <div className="col-md-6 mb-3">
                        <label htmlFor="invoiceNo" className="form-label">Invoice No</label>
                        <input
                          type="text"
                          className="form-control"
                          id="invoiceNo"
                          name="invoiceNo"
                          value={invoiceData.invoiceNo}
                          readOnly
                        />
                      </div>
                      <div className="col-md-6 mb-3">
                        <label htmlFor="partnerId" className="form-label">Partner</label>
                        <select
                          className="form-select"
                          id="partnerId"
                          name="partnerId"
                          value={invoiceData.partnerId}
                          onChange={handleChange}
                          required
                        >
                          <option value="">Select Partner</option>
                          {partners.map((partner) => (
                            <option key={partner.id} value={partner.id}>
                              {partner.name}
                            </option>
                          ))}
                        </select>
                      </div>
                    </div>
                    <div className="row">
                      <div className="col-md-6 mb-3">
                        <label htmlFor="invoiceDate" className="form-label">Invoice Date</label>
                        <input
                          type="date"
                          className="form-control"
                          id="invoiceDate"
                          name="invoiceDate"
                          value={invoiceData.invoiceDate}
                          onChange={handleChange}
                          required
                        />
                      </div>
                      <div className="col-md-6 mb-3">
                        <label htmlFor="dueDate" className="form-label">Due Date</label>
                        <input
                          type="date"
                          className="form-control"
                          id="dueDate"
                          name="dueDate"
                          value={invoiceData.dueDate}
                          onChange={handleChange}
                          required
                        />
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="text-center mt-4">
              <button type="submit" className="btn btn-success me-3">
                <i className="fa fa-check"></i> Submit
              </button>
              <button
                type="button"
                className="btn btn-danger"
                onClick={handleCancel}
              >
                <i className="fa fa-times"></i> Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddInvoice;
