import React, { useState, useEffect } from "react";
import NavBar from "./NavBar";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Spinner from "./Spinner";
import Alert from "./Alert";

const AddInvoice = ({ invoiceType }) => {
  const initialState = {
    type: invoiceType,
    invoiceDate: new Date().toISOString().split("T")[0],
    dueDate: new Date().toISOString().split("T")[0],
    invoiceNo: "",
    partnerId: "",
    creditTerm: 0,
    reference: "",
    invoiceTotal: 0,
    invoiceItems: [],
  };

  const [invoiceData, setInvoiceData] = useState(initialState);
  const [partners, setPartners] = useState([]);
  const [products, setProducts] = useState([]);
  const [productId, setProductId] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [isLoading, setIsLoading] = useState(true);
  const [alert, setAlert] = useState({ type: null, message: "" });

  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const partnerEndpoint =
          invoiceType === "sales" ? "/partners?type=customer" : "/partners?type=vendor";
        const invoiceEndpoint = `/invoices?type=${invoiceType}`;
        const productEndpoint = "/products";

        const [partnersRes, invoicesRes, productsRes] = await Promise.all([
          axios.get(partnerEndpoint),
          axios.get(invoiceEndpoint),
          axios.get(productEndpoint),
        ]);

        setPartners(partnersRes.data);
        setProducts(productsRes.data);

        const invoiceCount = invoicesRes.data.length;
        const newInvoiceNo =
          invoiceType === "sales"
            ? `SI-${String(invoiceCount + 1).padStart(4, "0")}`
            : `PI-${String(invoiceCount + 1).padStart(4, "0")}`;

        setInvoiceData((prevState) => ({ ...prevState, invoiceNo: newInvoiceNo }));
        setIsLoading(false);
      } catch (error) {
        setAlert({ type: "danger", message: `Error fetching data: ${error.message}` });
        setIsLoading(false);
      }
    };

    fetchData();
  }, [invoiceType]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoiceData((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleCreditTermChange = (e) => {
    const term = parseInt(e.target.value, 10) || 0;
    const newDueDate = new Date();
    newDueDate.setDate(newDueDate.getDate() + term);
    setInvoiceData((prevState) => ({
      ...prevState,
      creditTerm: term,
      dueDate: newDueDate.toISOString().split("T")[0],
    }));
  };

  const addProduct = () => {
    const selectedProduct = products.find((p) => p._id === productId);
    if (!selectedProduct) {
      setAlert({ type: "warning", message: "Product not found." });
      return;
    }

    const valueOfSupplies = selectedProduct.rate * quantity;
    const salesTax = (valueOfSupplies * selectedProduct.taxRate) / 100;
    const netAmount = valueOfSupplies + salesTax;

    setInvoiceData((prevState) => ({
      ...prevState,
      invoiceItems: [
        ...prevState.invoiceItems,
        {
          productId,
          productName: selectedProduct.name,
          quantity,
          rate: selectedProduct.rate,
          valueOfSupplies,
          salesTax,
          netAmount,
        },
      ],
      invoiceTotal: prevState.invoiceTotal + netAmount,
    }));

    setProductId("");
    setQuantity(1);
  };

  const handleRemoveInvoiceItem = (index) => {
    const items = [...invoiceData.invoiceItems];
    const removedItem = items.splice(index, 1);
    setInvoiceData((prevState) => ({
      ...prevState,
      invoiceItems: items,
      invoiceTotal: prevState.invoiceTotal - removedItem[0].netAmount,
    }));
  };

  const handleSubmit = async () => {
    try {
      await axios.post("/invoices", invoiceData);
      setAlert({ type: "success", message: "Invoice created successfully!" });
      navigate(`/${invoiceType === "sales" ? "salesinvoices" : "purchaseinvoices"}`);
    } catch (error) {
      setAlert({ type: "danger", message: `Error creating invoice: ${error.message}` });
    }
  };

  const handleCancel = () => {
    navigate(`/${invoiceType === "sales" ? "salesinvoices" : "purchaseinvoices"}`);
  };

  return isLoading ? (
    <Spinner />
  ) : (
    <div>
      <NavBar />
      <div className="container mt-4">
        {alert.message && <Alert type={alert.type} message={alert.message} />}
        <h3>Add {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice</h3>
        <div className="card shadow-lg p-4">
          <div className="mb-3">
            <label>Invoice No</label>
            <input className="form-control" value={invoiceData.invoiceNo} disabled />
          </div>
          <div className="mb-3">
            <label>Partner</label>
            <select
              className="form-control"
              name="partnerId"
              value={invoiceData.partnerId}
              onChange={handleChange}
            >
              <option value="">Select Partner</option>
              {partners.map((partner) => (
                <option key={partner._id} value={partner._id}>
                  {partner.firstName} {partner.lastName}
                </option>
              ))}
            </select>
          </div>
          <div className="mb-3">
            <label>Reference</label>
            <input
              className="form-control"
              name="reference"
              value={invoiceData.reference}
              onChange={handleChange}
            />
          </div>
          <div className="mb-3">
            <label>Credit Term (days)</label>
            <input
              type="number"
              className="form-control"
              name="creditTerm"
              value={invoiceData.creditTerm}
              onChange={handleCreditTermChange}
            />
          </div>
          <div className="mb-3">
            <label>Due Date</label>
            <input className="form-control" value={invoiceData.dueDate} disabled />
          </div>

          <h5>Invoice Items</h5>
          <div className="d-flex mb-3">
            <select
              className="form-control me-2"
              value={productId}
              onChange={(e) => setProductId(e.target.value)}
            >
              <option value="">Select Product</option>
              {products.map((product) => (
                <option key={product._id} value={product._id}>
                  {product.name}
                </option>
              ))}
            </select>
            <input
              type="number"
              className="form-control me-2"
              value={quantity}
              onChange={(e) => setQuantity(parseInt(e.target.value, 10) || 1)}
            />
            <button className="btn btn-primary" onClick={addProduct}>
              Add Item
            </button>
          </div>
          <table className="table">
            <thead>
              <tr>
                <th>Product</th>
                <th>Quantity</th>
                <th>Rate</th>
                <th>Value</th>
                <th>Tax</th>
                <th>Net Amount</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {invoiceData.invoiceItems.map((item, index) => (
                <tr key={index}>
                  <td>{item.productName}</td>
                  <td>{item.quantity}</td>
                  <td>{item.rate}</td>
                  <td>{item.valueOfSupplies.toFixed(2)}</td>
                  <td>{item.salesTax.toFixed(2)}</td>
                  <td>{item.netAmount.toFixed(2)}</td>
                  <td>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => handleRemoveInvoiceItem(index)}
                    >
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="d-flex justify-content-between">
            <button className="btn btn-success" onClick={handleSubmit}>
              Submit Invoice
            </button>
            <button className="btn btn-secondary" onClick={handleCancel}>
              Cancel
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddInvoice;
