import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Alert from "./Alert";

const AddInvoice = ({ invoiceType }) => {
  const navigate = useNavigate();

  // Initial state
  const [invoiceData, setInvoiceData] = useState({
    invoiceNo: "",
    partnerId: "",
    reference: "",
    invoiceDate: new Date().toISOString().split("T")[0],
    creditTerm: 0,
    dueDate: "",
    invoiceItems: [],
  });
  const [partners, setPartners] = useState([]);
  const [products, setProducts] = useState([]);
  const [productId, setProductId] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [alert, setAlert] = useState(null);

  // Fetch partners and generate invoice number
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        // Fetch partners
        const partnersResponse = await fetch(
          `/partners?type=${invoiceType === "sales" ? "customer" : "vendor"}`
        );
        const partnersData = await partnersResponse.json();
        setPartners(partnersData);

        // Fetch existing invoices to generate new invoiceNo
        const invoicesResponse = await fetch(
          `/invoices?type=${invoiceType}`
        );
        const invoicesData = await invoicesResponse.json();
        const nextInvoiceNumber = (invoicesData.length + 1).toString().padStart(4, "0");
        const invoicePrefix = invoiceType === "sales" ? "SI-" : "PI-";
        setInvoiceData((prev) => ({
          ...prev,
          invoiceNo: `${invoicePrefix}${nextInvoiceNumber}`,
        }));

        // Fetch products
        const productsResponse = await fetch("/products");
        const productsData = await productsResponse.json();
        const formattedProducts = productsData.map((product) => ({
          ...product,
          rate: Number(product.rate), // Ensure rate is a number
        }));
        setProducts(formattedProducts);
      } catch (error) {
        setAlert({ type: "error", message: "Failed to load initial data." });
      }
    };

    fetchInitialData();
  }, [invoiceType]);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoiceData((prev) => ({ ...prev, [name]: value }));
  };

  // Handle credit term changes and calculate due date
  const handleCreditTermChange = (e) => {
    const creditTerm = parseInt(e.target.value, 10) || 0;
    const dueDate = new Date();
    dueDate.setDate(dueDate.getDate() + creditTerm);

    setInvoiceData((prev) => ({
      ...prev,
      creditTerm,
      dueDate: dueDate.toISOString().split("T")[0],
    }));
  };

  // Add a product to the invoice
  const addProduct = () => {
    const selectedProduct = products.find((product) => product._id === productId);
    if (!selectedProduct) {
      setAlert({ type: "error", message: "Invalid product selection." });
      return;
    }

    const valueOfSupplies = quantity * selectedProduct.rate;
    const salesTax = (valueOfSupplies * selectedProduct.salesTax) / 100;
    const netAmount = valueOfSupplies + salesTax;

    setInvoiceData((prev) => ({
      ...prev,
      invoiceItems: [
        ...prev.invoiceItems,
        {
          productId,
          productName: selectedProduct.name,
          quantity,
          rate: selectedProduct.rate, // Already ensured to be a number
          valueOfSupplies,
          salesTax,
          netAmount,
        },
      ],
    }));
    setProductId("");
    setQuantity(1);
  };

  // Remove a product from the invoice
  const handleRemoveInvoiceItem = (index) => {
    setInvoiceData((prev) => ({
      ...prev,
      invoiceItems: prev.invoiceItems.filter((_, i) => i !== index),
    }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("/invoices", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(invoiceData),
      });

      if (response.ok) {
        setAlert({ type: "success", message: "Invoice added successfully!" });
        navigate(`/${invoiceType}invoices`);
      } else {
        setAlert({ type: "error", message: "Failed to add invoice." });
      }
    } catch (error) {
      setAlert({ type: "error", message: "An error occurred." });
    }
  };

  // Cancel and navigate back
  const handleCancel = () => navigate(`/${invoiceType}invoices`);

  return (
    <div className="container mt-5">
      {alert && <Alert type={alert.type} message={alert.message} className="mb-4" />}
      <div className="card shadow-lg border-0">
        <div className="card-header bg-gradient-primary text-white">
          <h3 className="text-center mb-0">
            Add {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice
          </h3>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <h5>Invoice Details</h5>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label htmlFor="invoiceNo" className="form-label">
                  Invoice Number
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="invoiceNo"
                  name="invoiceNo"
                  value={invoiceData.invoiceNo}
                  readOnly
                />
              </div>
              <div className="col-md-6 mb-3">
                <label htmlFor="partnerId" className="form-label">
                  {invoiceType === "sales" ? "Customer" : "Vendor"}
                </label>
                <select
                  className="form-select"
                  id="partnerId"
                  name="partnerId"
                  value={invoiceData.partnerId}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select Partner</option>
                  {partners.map((partner) => (
                    <option key={partner._id} value={partner._id}>
                      {partner.firstName} {partner.lastName}
                    </option>
                  ))}
                </select>
              </div>
            </div>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label htmlFor="creditTerm" className="form-label">
                  Credit Term (days)
                </label>
                <input
                  type="number"
                  className="form-control"
                  id="creditTerm"
                  name="creditTerm"
                  value={invoiceData.creditTerm}
                  onChange={handleCreditTermChange}
                />
              </div>
              <div className="col-md-6 mb-3">
                <label htmlFor="dueDate" className="form-label">
                  Due Date
                </label>
                <input
                  type="date"
                  className="form-control"
                  id="dueDate"
                  name="dueDate"
                  value={invoiceData.dueDate}
                  readOnly
                />
              </div>
            </div>

            <h5 className="mt-4">Invoice Items</h5>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label htmlFor="productId" className="form-label">Product</label>
                <select
                  className="form-select"
                  id="productId"
                  value={productId}
                  onChange={(e) => setProductId(e.target.value)}
                >
                  <option value="">Select Product</option>
                  {products.map((product) => (
                    <option key={product._id} value={product._id}>
                      {product.name}
                    </option>
                  ))}
                </select>
              </div>
              <div className="col-md-3 mb-3">
                <label htmlFor="quantity" className="form-label">Quantity</label>
                <input
                  type="number"
                  className="form-control"
                  id="quantity"
                  value={quantity}
                  onChange={(e) => setQuantity(e.target.value)}
                />
              </div>
              <div className="col-md-3 mb-3 d-flex align-items-end">
                <button type="button" className="btn btn-success w-100" onClick={addProduct}>
                  Add Product
                </button>
              </div>
            </div>

            {invoiceData.invoiceItems.length > 0 && (
              <table className="table table-striped table-hover mt-4">
                <thead>
                  <tr>
                    <th>Product</th>
                    <th>Quantity</th>
                    <th>Rate</th>
                    <th>Value</th>
                    <th>Tax</th>
                    <th>Net Amount</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {invoiceData.invoiceItems.map((item, index) => (
                    <tr key={index}>
                      <td>{item.productName}</td>
                      <td>{item.quantity}</td>
                      <td>${Number(item.rate).toFixed(2)}</td>
                      <td>${Number(item.valueOfSupplies).toFixed(2)}</td>
                      <td>${Number(item.salesTax).toFixed(2)}</td>
                      <td>${Number(item.netAmount).toFixed(2)}</td>
                      <td>
                        <button
                          type="button"
                          className="btn btn-sm btn-danger"
                          onClick={() => handleRemoveInvoiceItem(index)}
                        >
                          Remove
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}

            <div className="text-center mt-4">
              <button type="submit" className="btn btn-success me-3">
                Submit
              </button>
              <button
                type="button"
                className="btn btn-danger"
                onClick={handleCancel}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddInvoice;
