import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Alert from "./Alert";

const AddPartner = () => {
  const navigate = useNavigate();

  const [alert, setAlert] = useState({ type: null, message: "" });

  const initialFormState = {
    type: "customer",
    firstName: "",
    lastName: "",
    email: "",
    contactNo: "",
    status: "",
    address: "",
    openingBalance: "",
    representative: {
      name: "",
      contact: "",
      designation: "",
    },
  };

  const [formData, setFormData] = useState(initialFormState);

  const updateFormField = (e) => {
    const { name, value } = e.target;

    if (name.startsWith("rep")) {
      const field = name.replace("rep", "").toLowerCase();
      setFormData((prevState) => ({
        ...prevState,
        representative: { ...prevState.representative, [field]: value },
      }));
    } else {
      setFormData((prevState) => ({
        ...prevState,
        [name]: value,
      }));
    }
  };

  const sanitizeData = () => {
    const sanitizedData = { ...formData };
    sanitizedData.openingBalance = parseFloat(sanitizedData.openingBalance) || 0;
    sanitizedData.contactNo = parseInt(sanitizedData.contactNo, 10) || 0;
    sanitizedData.representative.contact =
      parseInt(sanitizedData.representative.contact, 10) || 0;
    return sanitizedData;
  };

  const submitForm = async (e) => {
    e.preventDefault();
    const dataToSubmit = sanitizeData();

    try {
      const response = await axios.post("/partners", dataToSubmit);
      if (response.status === 201) {
        setAlert({ type: "success", message: response.data });
        setFormData(initialFormState);
      } else {
        setAlert({
          type: "danger",
          message: `Failed to add partner: ${response.status} ${response.statusText}`,
        });
      }
    } catch (error) {
      setAlert({
        type: "danger",
        message: `Error: ${error.response?.data || error.message}`,
      });
    }
  };

  const cancelForm = () => {
    navigate("/partners");
  };

  return (
    <form onSubmit={submitForm} className="mt-4">
      <div className="row">
        <div className="col-md-6">
          <div className="mb-3">
            <label htmlFor="firstName" className="form-label">
              First Name
            </label>
            <input
              type="text"
              className="form-control"
              id="firstName"
              name="firstName"
              value={formData.firstName}
              onChange={updateFormField}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Email
            </label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              value={formData.email}
              onChange={updateFormField}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="status" className="form-label">
              Status
            </label>
            <select
              className="form-select"
              id="status"
              name="status"
              value={formData.status}
              onChange={updateFormField}
              required
            >
              <option value="">Select Status</option>
              <option value="Active">Active</option>
              <option value="Inactive">Inactive</option>
            </select>
          </div>
          <div className="mb-3">
            <label htmlFor="address" className="form-label">
              Address
            </label>
            <textarea
              className="form-control"
              id="address"
              name="address"
              value={formData.address}
              onChange={updateFormField}
            />
          </div>
        </div>
        <div className="col-md-6">
          <div className="mb-3">
            <label htmlFor="lastName" className="form-label">
              Last Name
            </label>
            <input
              type="text"
              className="form-control"
              id="lastName"
              name="lastName"
              value={formData.lastName}
              onChange={updateFormField}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="contactNo" className="form-label">
              Contact No.
            </label>
            <input
              type="text"
              className="form-control"
              id="contactNo"
              name="contactNo"
              value={formData.contactNo}
              onChange={updateFormField}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="openingBalance" className="form-label">
              Opening Balance
            </label>
            <input
              type="number"
              className="form-control"
              id="openingBalance"
              name="openingBalance"
              value={formData.openingBalance}
              onChange={updateFormField}
              required
            />
          </div>
        </div>
      </div>
      <h3>Representative Information</h3>
      <div className="row mt-4">
        <div className="col-md-6">
          <div className="mb-3">
            <label htmlFor="repName" className="form-label">
              Full Name
            </label>
            <input
              type="text"
              className="form-control"
              id="repName"
              name="repName"
              value={formData.representative.name}
              onChange={updateFormField}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="repContact" className="form-label">
              Contact No.
            </label>
            <input
              type="text"
              className="form-control"
              id="repContact"
              name="repContact"
              value={formData.representative.contact}
              onChange={updateFormField}
              required
            />
          </div>
        </div>
        <div className="col-md-6">
          <div className="mb-3">
            <label htmlFor="repDesignation" className="form-label">
              Designation
            </label>
            <input
              type="text"
              className="form-control"
              id="repDesignation"
              name="repDesignation"
              value={formData.representative.designation}
              onChange={updateFormField}
              required
            />
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-md-12">
          <button type="submit" className="btn btn-outline-success me-2">
            Save
          </button>
          <button
            type="button"
            className="btn btn-outline-danger"
            onClick={cancelForm}
          >
            Cancel
          </button>
        </div>
      </div>
      {alert.message && <Alert type={alert.type} message={alert.message} />}
    </form>
  );
};

export default AddPartner;
