import React, { useState, useEffect } from "react";
import NavBar from "./NavBar";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Spinner from "./Spinner";
import Alert from "./Alert";

const AddInvoice = ({ invoiceType }) => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const [alertType, setAlertType] = useState(null);
  const [alertMessage, setAlertMessage] = useState("");
  const [partners, setPartners] = useState([]); // إذا كان ضرورياً
  const [products, setProducts] = useState([]);
  const [page, setPage] = useState(1);
  const [productId, setProductId] = useState("");
  const [quantity, setQuantity] = useState(0);

  const initialState = {
    type: invoiceType,
    invoiceDate: new Date().toISOString().split("T")[0],
    dueDate: new Date().toISOString().split("T")[0],
    invoiceNo: "",
    partnerId: "",
    creditTerm: 0,
    reference: "",
    invoiceTotal: 0,
    invoiceItems: [],
  };

  const [invoiceData, setInvoiceData] = useState(initialState);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [invoiceNumberResponse, partnersResponse, productsResponse] =
          await Promise.all([
            axios.get(`/invoices?type=${invoiceType}`),
            axios.get(
              `/partners?type=${
                invoiceType === "sales" ? "customer" : "vendor"
              }&status=Active`
            ),
            axios.get(`/products?status=Active`),
          ]);

        if (invoiceNumberResponse.status === 200) {
          const totalInvoices = invoiceNumberResponse.data.length;
          const nextInvoiceNumber = `${
            invoiceType === "sales" ? "SI" : "PI"
          }-${String(totalInvoices + 1).padStart(4, "0")}`;
          setInvoiceData((prevData) => ({
            ...prevData,
            invoiceNo: nextInvoiceNumber,
          }));
        }

        if (partnersResponse.status === 200) {
          setPartners(partnersResponse.data);
        }

        if (productsResponse.status === 200) {
          setProducts(productsResponse.data);
        }

        setIsLoading(false);
      } catch (error) {
        setAlertType("danger");
        setAlertMessage(`Failed to fetch data: ${error.message}`);
        setIsLoading(false);
      }
    };

    fetchData();
  }, [invoiceType]);

  const addProduct = () => {
    if (productId && quantity) {
      const selectedProduct = products.find(
        (product) => product._id === productId
      );

      if (selectedProduct) {
        const valueOfSupplies = quantity * selectedProduct.rate;
        const salesTax = (valueOfSupplies * selectedProduct.salesTax) / 100;
        const netAmount = valueOfSupplies + salesTax;

        const newItem = {
          productName: selectedProduct.name,
          quantity,
          rate: selectedProduct.rate,
          valueOfSupplies: valueOfSupplies.toFixed(2),
          salesTax: salesTax.toFixed(2),
          netAmount: netAmount.toFixed(2),
        };

        setInvoiceData((prevData) => ({
          ...prevData,
          invoiceTotal: prevData.invoiceTotal + netAmount,
          invoiceItems: [...prevData.invoiceItems, newItem],
        }));

        setProductId("");
        setQuantity(0);
      } else {
        alert("Selected product not found.");
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/invoices", invoiceData);
      if (response.status === 201) {
        setAlertType("success");
        setAlertMessage("Invoice added successfully.");
        setInvoiceData(initialState);
        setPage(1);
      }
    } catch (error) {
      setAlertType("danger");
      setAlertMessage(`Failed to add invoice: ${error.message}`);
    }
  };

  return (
    <div>
      <NavBar />
      <div className="container mt-4">
        {isLoading ? (
          <Spinner />
        ) : (
          <>
            <h2>Create {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice</h2>
            <form onSubmit={handleSubmit}>
              {page === 2 && (
                <div>
                  <h3>Invoice Items</h3>
                  <div className="mb-3">
                    <label htmlFor="productId" className="form-label">Product</label>
                    <select
                      id="productId"
                      name="productId"
                      className="form-select"
                      value={productId}
                      onChange={(e) => setProductId(e.target.value)}
                    >
                      <option value="">Select Product</option>
                      {products.map((product) => (
                        <option key={product._id} value={product._id}>
                          {product.name}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div className="mb-3">
                    <label htmlFor="quantity" className="form-label">Quantity</label>
                    <input
                      type="number"
                      id="quantity"
                      name="quantity"
                      className="form-control"
                      value={quantity}
                      onChange={(e) => setQuantity(e.target.value)}
                      min="1"
                      required
                    />
                  </div>
                  <button
                    type="button"
                    className="btn btn-primary"
                    onClick={addProduct}
                  >
                    Add Product
                  </button>
                </div>
              )}
            </form>
          </>
        )}
      </div>
    </div>
  );
};

export default AddInvoice;
