import React, { useEffect, useState } from "react";
import NavBar from "./NavBar";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import Spinner from "./Spinner";
import Alert from "./Alert";

const Partners = ({ partnerType }) => {
  const [partners, setPartners] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPartners = async () => {
      try {
        const response = await axios.get(`/partners?type=${partnerType}`);
        setPartners(response.data);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch partners");
        setLoading(false);
      }
    };

    fetchPartners();
  }, [partnerType]);

  const handleEditPartner = (id) => {
    navigate(`/edit-partner?id=${id}`);
  };

  return (
    <div>
      <NavBar />
      <div className="container mt-4">
        {loading ? (
          <Spinner />
        ) : error ? (
          <Alert message={error} />
        ) : (
          <>
            <div className="d-flex justify-content-between mb-3">
              <h2>Partners</h2>
              <Link to="/add-partner" className="btn btn-primary">
                Add {partnerType === 'customer' ? 'Customer' : 'Vendor'}
              </Link>
            </div>

            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>Full Name</th>
                  <th>Email</th>
                  <th>Contact No</th>
                  <th>Status</th>
                  <th>Opening Balance</th>
                  <th>Rep Name</th>
                  <th>Rep Contact</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {partners.map((partner) => (
                  <tr key={partner.id}>
                    <td>{partner.fullName}</td>
                    <td>{partner.email}</td>
                    <td>{partner.contactNo}</td>
                    <td>{partner.status}</td>
                    <td>{partner.openingBalance}</td>
                    <td>{partner.repName}</td>
                    <td>{partner.repContact}</td>
                    <td>
                      <button
                        className="btn btn-warning"
                        onClick={() => handleEditPartner(partner.id)}
                      >
                        Edit
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </>
        )}
      </div>
    </div>
  );
};

export default Partners;
