import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Spinner from "./Spinner";
import Alert from "./Alert";

const AddInvoice = ({ invoiceType }) => {
  const navigate = useNavigate();

  // State variables for alert
  const [alertType, setAlertType] = useState(null);
  const [alertMessage, setAlertMessage] = useState("");

  // Loading state
  const [isLoading, setIsLoading] = useState(true);

  // Partners and Products state
  const [partners, setPartners] = useState([]);
  const [products, setProducts] = useState([]);

  // Invoice state
  const [invoiceData, setInvoiceData] = useState({
    type: invoiceType,
    invoiceNo: "",
    partnerId: "",
    creditTerm: 0,
    invoiceDate: "",
    dueDate: "",
    invoiceItems: [],
    invoiceTotal: 0,
    reference: "",
  });

  // Page navigation state
  const [page, setPage] = useState(1);

  // Local state for managing product input
  const [productId, setProductId] = useState("");
  const [quantity, setQuantity] = useState(0);

  // Progress bar width
  const progressBarWidth = (page === 1 ? 50 : 100) + "%";

  // Fetch initial data
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [invoiceNumberResponse, partnersResponse, productsResponse] =
          await Promise.all([
            axios.get(`/invoices?type=${invoiceType}`),
            axios.get(
              `/partners?type=${
                invoiceType === "sales" ? "customer" : "vendor"
              }&status=Active`
            ),
            axios.get(`/products?status=Active`),
          ]);

        // Handle invoice number
        if (invoiceNumberResponse.status === 200) {
          const totalInvoices = invoiceNumberResponse.data.length;
          const nextInvoiceNumber = `${
            invoiceType === "sales" ? "SI" : "PI"
          }-${String(totalInvoices + 1).padStart(4, "0")}`;
          setInvoiceData((prevData) => ({
            ...prevData,
            invoiceNo: nextInvoiceNumber,
          }));
        } else {
          setAlertType("danger");
          setAlertMessage("Failed to generate Invoice No.");
        }

        // Handle partners
        if (partnersResponse.status === 200) {
          setPartners(partnersResponse.data);
        } else {
          setAlertType("danger");
          setAlertMessage(
            `${invoiceType === "sales" ? "Customers" : "Vendors"} not found.`
          );
        }

        // Handle products
        if (productsResponse.status === 200) {
          setProducts(productsResponse.data);
        } else {
          setAlertType("danger");
          setAlertMessage("Failed to retrieve products.");
        }

        setIsLoading(false);
      } catch (error) {
        setAlertType("danger");
        setAlertMessage(`Failed to fetch data: ${error.message}`);
        setIsLoading(false);
      }
    };

    fetchData();
  }, [invoiceType]);

  // Add product to invoice
  const addProduct = () => {
    if (productId && quantity) {
      const selectedProduct = products.find(
        (product) => product._id === productId
      );

      if (selectedProduct) {
        const valueOfSupplies = quantity * selectedProduct.rate;
        const salesTax = (valueOfSupplies * selectedProduct.salesTax) / 100;
        const netAmount = valueOfSupplies + salesTax;

        const newItem = {
          productName: selectedProduct.name,
          quantity,
          rate: selectedProduct.rate,
          valueOfSupplies: valueOfSupplies.toFixed(2),
          salesTax: salesTax.toFixed(2),
          netAmount: netAmount.toFixed(2),
        };

        setInvoiceData((prevData) => ({
          ...prevData,
          invoiceTotal: prevData.invoiceTotal + netAmount,
          invoiceItems: [...prevData.invoiceItems, newItem],
        }));

        // Reset input fields
        setProductId("");
        setQuantity(0);
      } else {
        alert("Selected product not found.");
      }
    }
  };

  // Remove product from invoice
  const handleRemoveInvoiceItem = (itemToRemove) => {
    const updatedInvoiceItems = invoiceData.invoiceItems.filter(
      (item) => item !== itemToRemove
    );

    const newInvoiceTotal = updatedInvoiceItems.reduce(
      (total, item) => total + parseFloat(item.netAmount),
      0
    );

    setInvoiceData((prevData) => ({
      ...prevData,
      invoiceTotal: newInvoiceTotal,
      invoiceItems: updatedInvoiceItems,
    }));
  };

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoiceData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Calculate due date based on credit term
  const handleCreditTermChange = (e) => {
    const newCreditTerm = e.target.value === "" ? 0 : parseInt(e.target.value);
    const currentDate = new Date();
    const newDueDate = new Date(
      currentDate.getTime() + newCreditTerm * 24 * 60 * 60 * 1000
    )
      .toISOString()
      .split("T")[0];

    setInvoiceData((prevData) => ({
      ...prevData,
      creditTerm: newCreditTerm,
      dueDate: newDueDate,
    }));
  };

  // Submit invoice
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("/invoices", invoiceData);

      if (response.status === 201) {
        setAlertType("success");
        setAlertMessage("Invoice added successfully!");
        setInvoiceData({
          type: invoiceType,
          invoiceNo: "",
          partnerId: "",
          creditTerm: 0,
          invoiceDate: "",
          dueDate: "",
          invoiceItems: [],
          invoiceTotal: 0,
          reference: "",
        });
        setPage(1);
      } else {
        setAlertType("danger");
        setAlertMessage("Failed to add invoice.");
      }
    } catch (error) {
      setAlertType("danger");
      setAlertMessage(`Failed to add invoice: ${error.message}`);
    }
  };

  // Cancel action
  const handleCancel = () => {
    navigate(`/${invoiceType}invoices`);
  };

  // Navigate between pages
  const handleNavigation = () => {
    setPage(page === 1 ? 2 : 1);
  };

  return (
    <div className="container mt-4">
      {isLoading ? (
        <Spinner />
      ) : (
        <>
          <h2>
            Create {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice
          </h2>
          <div className="progress mt-4 mb-4">
            <div
              className="progress-bar progress-bar-striped progress-bar-animated"
              style={{ width: progressBarWidth }}
            >
              {page === 1 ? "Step 1" : "Step 2"}
            </div>
          </div>
          <form onSubmit={handleSubmit}>
            {page === 1 && (
              <StepOne
                invoiceData={invoiceData}
                partners={partners}
                handleChange={handleChange}
                handleCreditTermChange={handleCreditTermChange}
              />
            )}
            {page === 2 && (
              <StepTwo
                invoiceData={invoiceData}
                products={products}
                addProduct={addProduct}
                handleRemoveInvoiceItem={handleRemoveInvoiceItem}
                productId={productId}
                setProductId={setProductId}
                quantity={quantity}
                setQuantity={setQuantity}
              />
            )}
            <NavigationButtons
              page={page}
              handleNavigation={handleNavigation}
              handleCancel={handleCancel}
            />
          </form>
          {alertMessage && <Alert type={alertType} message={alertMessage} />}
        </>
      )}
    </div>
  );
};

export default AddInvoice;
