import "./App.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Home from "./components/Home.jsx";
import Partners from "./components/Partners";
import AddPartner from "./components/AddPartner";
import EditPartner from "./components/EditPartner";
import Products from "./components/Products";
import AddProduct from "./components/AddProduct";
import EditProduct from "./components/EditProduct";
import Invoices from "./components/Invoices";
import AddInvoice from "./components/AddInvoice";
import InvoiceReport from "./components/InvoiceReport";
import Transactions from "./components/Transactions";
import AddTransaction from "./components/AddTransaction";
import Ledger from "./components/Ledger";

function App() {
  // وظيفة مساعدة لإنشاء مسارات الفواتير
  const renderInvoiceRoutes = (type) => (
    <>
      <Route
        path={`/${type}invoices`}
        element={<Invoices invoiceType={type} />}
      />
      <Route
        path={`/${type}invoices/add`}
        element={<AddInvoice invoiceType={type} />}
      />
      <Route
        path={`/${type}invoices/view`}
        element={<InvoiceReport invoiceType={type} />}
      />
    </>
  );

  // وظيفة مساعدة لإنشاء مسارات الشركاء
  const renderPartnerRoutes = (type) => (
    <>
      <Route
        path={`/${type}`}
        element={<Partners partnerType={type.slice(0, -1)} />}
      />
      <Route
        path={`/${type}/add`}
        element={<AddPartner partnerType={type.slice(0, -1)} />}
      />
      <Route
        path={`/${type}/edit`}
        element={<EditPartner partnerType={type.slice(0, -1)} />}
      />
    </>
  );

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Home />} />
        {renderPartnerRoutes("customers")}
        {renderPartnerRoutes("vendors")}
        <Route path="/products" element={<Products />} />
        <Route path="/products/add" element={<AddProduct />} />
        <Route path="/products/edit" element={<EditProduct />} />
        {renderInvoiceRoutes("sales")}
        {renderInvoiceRoutes("purchase")}
        <Route
          path="/payments"
          element={<Transactions transactionType={"payment"} />}
        />
        <Route
          path="/payments/add"
          element={<AddTransaction transactionType={"payment"} />}
        />
        <Route
          path="/receipts"
          element={<Transactions transactionType={"receipt"} />}
        />
        <Route
          path="/receipts/add"
          element={<AddTransaction transactionType={"receipt"} />}
        />
        <Route path="/ledger" element={<Ledger />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
