import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Alert from "./Alert";
import axios from "axios";

const AddTransaction = ({ transactionType }) => {
  const initialState = {
    voucherNo: "",
    type: "",
    date: new Date().toISOString().split("T")[0],
    partnerId: "",
    amount: "",
    reference: "",
  };

  const [transactionData, setTransactionData] = useState(initialState);
  const [partners, setPartners] = useState([]);
  const [alert, setAlert] = useState(null);
  const navigate = useNavigate();

  // Fetch partner data and generate voucher number
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [partnersResponse, transactionsResponse] = await Promise.all([
          axios.get(
            `/partners?type=${
              transactionType === "receipt" ? "customer" : "vendor"
            }&status=Active`
          ),
          axios.get(`/transactions?type=${transactionType}`),
        ]);

        // Set partners data
        setPartners(partnersResponse.data);

        // Generate voucher number
        const totalTransactions = transactionsResponse.data.length;
        const nextVoucherNumber = `${
          transactionType === "payment" ? "PMT" : "RCT"
        }-${String(totalTransactions + 1).padStart(4, "0")}`;

        setTransactionData((prev) => ({
          ...prev,
          voucherNo: nextVoucherNumber,
        }));
      } catch (error) {
        setAlert({
          type: "error",
          message: "Failed to load initial data. Please try again.",
        });
      }
    };

    fetchData();
  }, [transactionType]);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setTransactionData((prev) => ({ ...prev, [name]: value }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/transactions", transactionData);
      if (response.status === 201) {
        setAlert({
          type: "success",
          message: "Transaction added successfully!",
        });
        setTransactionData(initialState);
        navigate(`/${transactionType}s`);
      }
    } catch (error) {
      setAlert({
        type: "error",
        message: error.response?.data?.message || "Failed to add transaction.",
      });
    }
  };

  // Handle cancel action
  const handleCancel = () => {
    navigate(`/${transactionType}s`);
  };

  return (
    <div className="container mt-5">
      {alert && <Alert type={alert.type} message={alert.message} />}
      <div className="card shadow-lg border-0">
        <div className="card-header bg-gradient-primary text-white">
          <h3 className="text-center mb-0">
            Add {transactionType === "payment" ? "Payment" : "Receipt"} Transaction
          </h3>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="accordion" id="transactionFormAccordion">
              {/* Transaction Details */}
              <div className="accordion-item">
                <h2 className="accordion-header" id="transactionDetailsHeading">
                  <button
                    className="accordion-button"
                    type="button"
                    data-bs-toggle="collapse"
                    data-bs-target="#transactionDetails"
                    aria-expanded="true"
                    aria-controls="transactionDetails"
                  >
                    Transaction Details
                  </button>
                </h2>
                <div
                  id="transactionDetails"
                  className="accordion-collapse collapse show"
                  aria-labelledby="transactionDetailsHeading"
                >
                  <div className="accordion-body">
                    <div className="row">
                      <div className="col-md-6 mb-3">
                        <label htmlFor="voucherNo" className="form-label">
                          Voucher Number
                        </label>
                        <input
                          type="text"
                          className="form-control"
                          id="voucherNo"
                          name="voucherNo"
                          value={transactionData.voucherNo}
                          readOnly
                        />
                      </div>
                      <div className="col-md-6 mb-3">
                        <label htmlFor="type" className="form-label">
                          Transaction Type
                        </label>
                        <select
                          className="form-select"
                          id="type"
                          name="type"
                          value={transactionData.type}
                          onChange={handleChange}
                          required
                        >
                          <option value="">Select Type</option>
                          {transactionType === "payment" ? (
                            <>
                              <option value="Bank Payment Voucher">
                                Bank Payment Voucher
                              </option>
                              <option value="Cash Payment Voucher">
                                Cash Payment Voucher
                              </option>
                            </>
                          ) : (
                            <>
                              <option value="Bank Receipt Voucher">
                                Bank Receipt Voucher
                              </option>
                              <option value="Cash Receipt Voucher">
                                Cash Receipt Voucher
                              </option>
                            </>
                          )}
                        </select>
                      </div>
                    </div>
                    <div className="row">
                      <div className="col-md-6 mb-3">
                        <label htmlFor="partnerId" className="form-label">
                          {transactionType === "payment"
                            ? "Payment To"
                            : "Receipt From"}
                        </label>
                        <select
                          className="form-select"
                          id="partnerId"
                          name="partnerId"
                          value={transactionData.partnerId}
                          onChange={handleChange}
                          required
                        >
                          <option value="">
                            Select{" "}
                            {transactionType === "payment"
                              ? "Vendor"
                              : "Customer"}
                          </option>
                          {partners.map((partner) => (
                            <option key={partner._id} value={partner._id}>
                              {partner.firstName} {partner.lastName}
                            </option>
                          ))}
                        </select>
                      </div>
                      <div className="col-md-6 mb-3">
                        <label htmlFor="date" className="form-label">
                          Date
                        </label>
                        <input
                          type="date"
                          className="form-control"
                          id="date"
                          name="date"
                          value={transactionData.date}
                          onChange={handleChange}
                          required
                        />
                      </div>
                    </div>
                    <div className="row">
                      <div className="col-md-6 mb-3">
                        <label htmlFor="amount" className="form-label">
                          Amount
                        </label>
                        <input
                          type="number"
                          className="form-control"
                          id="amount"
                          name="amount"
                          placeholder="Enter amount"
                          value={transactionData.amount}
                          onChange={handleChange}
                          required
                          min="1"
                        />
                      </div>
                      <div className="col-md-6 mb-3">
                        <label htmlFor="reference" className="form-label">
                          Reference
                        </label>
                        <textarea
                          className="form-control"
                          id="reference"
                          name="reference"
                          rows="2"
                          placeholder="Enter reference"
                          value={transactionData.reference}
                          onChange={handleChange}
                        ></textarea>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Submit & Cancel Buttons */}
            <div className="text-center mt-4">
              <button type="submit" className="btn btn-success me-3">
                <i className="fa fa-check"></i> Submit
              </button>
              <button
                type="button"
                className="btn btn-danger"
                onClick={handleCancel}
              >
                <i className="fa fa-times"></i> Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddTransaction;
