import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Alert from "./Alert";

const AddInvoice = ({ invoiceType }) => {
  const navigate = useNavigate();

  // الحالة الافتراضية
  const initialState = {
    invoiceNo: "",
    invoiceDate: new Date().toISOString().split("T")[0],
    dueDate: "",
    partnerId: "",
    creditTerm: 0,
    reference: "",
    invoiceItems: [],
    invoiceTotal: 0,
  };

  const [invoiceData, setInvoiceData] = useState(initialState);
  const [partners, setPartners] = useState([]);
  const [products, setProducts] = useState([]);
  const [productId, setProductId] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [alert, setAlert] = useState(null);
  const [page, setPage] = useState(1); // تتبع الخطوة الحالية

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [partnersResponse, productsResponse] = await Promise.all([
          axios.get(`/partners?type=${invoiceType === "sales" ? "customer" : "vendor"}`),
          axios.get(`/products`),
        ]);

        if (partnersResponse.status === 200) setPartners(partnersResponse.data);
        if (productsResponse.status === 200) setProducts(productsResponse.data);

        const invoiceNumberResponse = await axios.get(`/invoices?type=${invoiceType}`);
        if (invoiceNumberResponse.status === 200) {
          const totalInvoices = invoiceNumberResponse.data.length;
          const nextInvoiceNo = `${invoiceType === "sales" ? "SI" : "PI"}-${String(
            totalInvoices + 1
          ).padStart(4, "0")}`;
          setInvoiceData((prevData) => ({ ...prevData, invoiceNo: nextInvoiceNo }));
        }
      } catch (error) {
        setAlert({ type: "danger", message: "Failed to fetch data." });
      }
    };

    fetchData();
  }, [invoiceType]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoiceData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleCreditTermChange = (e) => {
    const creditTerm = parseInt(e.target.value || 0);
    const dueDate = new Date(
      new Date(invoiceData.invoiceDate).getTime() + creditTerm * 24 * 60 * 60 * 1000
    )
      .toISOString()
      .split("T")[0];
    setInvoiceData((prevData) => ({ ...prevData, creditTerm, dueDate }));
  };

  const addProduct = () => {
    const product = products.find((prod) => prod._id === productId);
    if (product && quantity > 0) {
      const valueOfSupplies = product.rate * quantity;
      const salesTax = (valueOfSupplies * product.salesTax) / 100;
      const netAmount = valueOfSupplies + salesTax;

      const newItem = {
        productName: product.name,
        quantity,
        rate: product.rate,
        valueOfSupplies,
        salesTax,
        netAmount,
      };

      setInvoiceData((prevData) => ({
        ...prevData,
        invoiceItems: [...prevData.invoiceItems, newItem],
        invoiceTotal: prevData.invoiceTotal + netAmount,
      }));
      setProductId("");
      setQuantity(1);
    }
  };

  const handleRemoveItem = (index) => {
    const items = [...invoiceData.invoiceItems];
    const removedItem = items.splice(index, 1)[0];
    setInvoiceData((prevData) => ({
      ...prevData,
      invoiceItems: items,
      invoiceTotal: prevData.invoiceTotal - removedItem.netAmount,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`/invoices`, invoiceData);
      if (response.status === 201) {
        setAlert({ type: "success", message: "Invoice added successfully!" });
        navigate(`/${invoiceType}invoices`);
      }
    } catch (error) {
      setAlert({ type: "danger", message: "Failed to add invoice." });
    }
  };

  const handleCancel = () => navigate(`/${invoiceType}invoices`);

  return (
    <div className="container mt-5">
      {alert && <Alert type={alert.type} message={alert.message} />}
      <div className="card shadow-lg border-0">
        <div className="card-header bg-gradient-primary text-white">
          <h3 className="text-center mb-0">
            Add {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice
          </h3>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            {page === 1 && (
              <>
                <h4>Invoice Details</h4>
                <div className="mb-3">
                  <label htmlFor="partnerId" className="form-label">
                    {invoiceType === "sales" ? "Customer" : "Vendor"} Name
                  </label>
                  <select
                    className="form-select"
                    name="partnerId"
                    value={invoiceData.partnerId}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select</option>
                    {partners.map((partner) => (
                      <option key={partner._id} value={partner._id}>
                        {partner.firstName} {partner.lastName}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="row">
                  <div className="col-md-6 mb-3">
                    <label htmlFor="invoiceNo" className="form-label">
                      Invoice No.
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      value={invoiceData.invoiceNo}
                      readOnly
                    />
                  </div>
                  <div className="col-md-6 mb-3">
                    <label htmlFor="dueDate" className="form-label">
                      Due Date
                    </label>
                    <input
                      type="date"
                      className="form-control"
                      value={invoiceData.dueDate}
                      readOnly
                    />
                  </div>
                </div>
              </>
            )}
            {page === 2 && (
              <>
                <h4>Invoice Items</h4>
                <div className="row mb-3">
                  <div className="col-md-6">
                    <label htmlFor="productId" className="form-label">
                      Product
                    </label>
                    <select
                      className="form-select"
                      value={productId}
                      onChange={(e) => setProductId(e.target.value)}
                    >
                      <option value="">Select Product</option>
                      {products.map((product) => (
                        <option key={product._id} value={product._id}>
                          {product.name}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div className="col-md-3">
                    <label htmlFor="quantity" className="form-label">
                      Quantity
                    </label>
                    <input
                      type="number"
                      className="form-control"
                      value={quantity}
                      min="1"
                      onChange={(e) => setQuantity(e.target.value)}
                    />
                  </div>
                  <div className="col-md-3 d-flex align-items-end">
                    <button
                      type="button"
                      className="btn btn-success w-100"
                      onClick={addProduct}
                    >
                      Add Item
                    </button>
                  </div>
                </div>
                <table className="table table-bordered">
                  <thead>
                    <tr>
                      <th>Product</th>
                      <th>Quantity</th>
                      <th>Rate</th>
                      <th>Value</th>
                      <th>Tax</th>
                      <th>Total</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {invoiceData.invoiceItems.map((item, index) => (
                      <tr key={index}>
                        <td>{item.productName}</td>
                        <td>{item.quantity}</td>
                        <td>{item.rate}</td>
                        <td>{item.valueOfSupplies}</td>
                        <td>{item.salesTax}</td>
                        <td>{item.netAmount}</td>
                        <td>
                          <button
                            type="button"
                            className="btn btn-danger"
                            onClick={() => handleRemoveItem(index)}
                          >
                            Remove
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            )}
            <div className="d-flex justify-content-between">
              {page > 1 && (
                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={() => setPage(page - 1)}
                >
                  Previous
                </button>
              )}
              {page < 2 && (
                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={() => setPage(page + 1)}
                >
                  Next
                </button>
              )}
              {page === 2 && (
                <>
                  <button type="submit" className="btn btn-success">
                    Submit
                  </button>
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={handleCancel}
                  >
                    Cancel
                  </button>
                </>
              )}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddInvoice;
