import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Alert from "./Alert";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [alert, setAlert] = useState(null);
  const navigate = useNavigate();

  // Fetch products from the server when the component mounts
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("/products");
        if (response.ok) {
          const data = await response.json();
          setProducts(data);
        } else {
          const errorData = await response.json();
          setAlert({ type: "error", message: errorData.message || "Failed to fetch products." });
        }
      } catch (error) {
        setAlert({ type: "error", message: "An error occurred while fetching products." });
      }
    };

    fetchProducts();
  }, []);

  // Handle edit product action
  const handleEditProduct = (id) => {
    navigate(`/edit-product?id=${id}`);
  };

  // Redirect to the add product page
  const handleAddProduct = () => {
    navigate("/add-product");
  };

  return (
    <div className="container mt-5">
      {alert && <Alert type={alert.type} message={alert.message} className="mb-4" />}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="text-primary">Products</h2>
        <button className="btn btn-success" onClick={handleAddProduct}>
          <i className="fa fa-plus"></i> Add Product
        </button>
      </div>
      <div className="card shadow-lg border-0">
        <div className="card-body">
          {products.length > 0 ? (
            <table className="table table-striped">
              <thead className="table-primary">
                <tr>
                  <th>Name</th>
                  <th>Core Company</th>
                  <th>Rate</th>
                  <th>Status</th>
                  <th>Tax Exempted</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {products.map((product) => (
                  <tr key={product.id}>
                    <td>{product.name}</td>
                    <td>{product.coreCompany}</td>
                    <td>{product.rate}</td>
                    <td>{product.status}</td>
                    <td>{product.taxExempted}</td>
                    <td>
                      <button
                        className="btn btn-primary btn-sm"
                        onClick={() => handleEditProduct(product.id)}
                      >
                        <i className="fa fa-edit"></i> Edit
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p className="text-center">No products available.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Products;
