import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import Alert from "./Alert";
import Spinner from "./Spinner";

const Invoices = ({ invoiceType }) => {
  const navigate = useNavigate();

  // State variables
  const [invoices, setInvoices] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [alertType, setAlertType] = useState(null);
  const [alertMessage, setAlertMessage] = useState("");

  // Fetch invoices from server
  useEffect(() => {
    const fetchInvoices = async () => {
      try {
        const response = await axios.get(`/invoices?type=${invoiceType}`);

        if (response.status === 200) {
          if (response.data.length === 0) {
            setAlertType("warning");
            setAlertMessage(`No ${invoiceType} invoices found.`);
          } else {
            setInvoices(response.data);
          }
        } else {
          setAlertType("danger");
          setAlertMessage(`Failed to retrieve ${invoiceType} invoices.`);
        }
      } catch (error) {
        setAlertType("danger");
        setAlertMessage(`Error: ${error.message}`);
      } finally {
        setIsLoading(false);
      }
    };

    fetchInvoices();
  }, [invoiceType]);

  const handleViewInvoice = (invoice) => {
    navigate(`/${invoiceType}invoices/view?id=${invoice._id}`);
  };

  return (
    <div className="container mt-5">
      {isLoading ? (
        <Spinner />
      ) : (
        <div className="card shadow-lg border-0">
          <div className="card-header bg-gradient-primary text-white">
            <h3 className="text-center mb-0">
              {invoiceType === "sales" ? "Sales Invoices" : "Purchase Invoices"}
            </h3>
          </div>
          <div className="card-body">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h5 className="mb-0">
                {invoiceType === "sales" ? "Manage Sales Invoices" : "Manage Purchase Invoices"}
              </h5>
              <Link to="add" className="btn btn-success">
                <i className="fa fa-plus"></i> Add {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice
              </Link>
            </div>

            {alertMessage ? (
              <Alert type={alertType} message={alertMessage} />
            ) : (
              <div className="table-responsive">
                <table className="table table-striped table-hover">
                  <thead style={{ backgroundColor: "#f2f7fc", color: "#000" }}>
                    <tr>
                      <th>Invoice Date</th>
                      <th>Invoice No</th>
                      <th>Due Date</th>
                      <th>{invoiceType === "sales" ? "Customer" : "Vendor"} Name</th>
                      <th>Total Items</th>
                      <th>Invoice Total</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {invoices.map((invoice) => (
                      <tr key={invoice._id}>
                        <td>{invoice.invoiceDate || "N/A"}</td>
                        <td>{invoice.invoiceNo || "N/A"}</td>
                        <td>{invoice.dueDate || "N/A"}</td>
                        <td>
                          {invoice.partner
                            ? `${invoice.partner.firstName} ${invoice.partner.lastName}`
                            : "Unknown Partner"}
                        </td>
                        <td>{invoice.invoiceItems?.length || 0}</td>
                        <td>
                          $
                          {invoice.invoiceTotal
                            ? parseFloat(invoice.invoiceTotal).toFixed(2)
                            : "0.00"}
                        </td>
                        <td>
                          <button
                            className="btn btn-sm btn-outline-primary"
                            onClick={() => handleViewInvoice(invoice)}
                          >
                            <i className="fa fa-eye"></i> View
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Invoices;
