import React, { useState, useEffect } from "react";
import NavBar from "./NavBar";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Spinner from "./Spinner";
import Alert from "./Alert";

const AddInvoice = ({ invoiceType }) => {
  const initialState = {
    type: invoiceType,
    invoiceDate: new Date().toISOString().split("T")[0],
    dueDate: new Date().toISOString().split("T")[0],
    invoiceNo: "",
    partnerId: "",
    creditTerm: 0,
    reference: "",
    invoiceTotal: 0,
    invoiceItems: [],
  };

  const [invoiceData, setInvoiceData] = useState(initialState);
  const [partners, setPartners] = useState([]);
  const [products, setProducts] = useState([]);
  const [productId, setProductId] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [isLoading, setIsLoading] = useState(true);
  const [alert, setAlert] = useState({ type: null, message: "" });

  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const partnerEndpoint =
          invoiceType === "sales" ? "/partners?type=customer" : "/partners?type=vendor";
        const productEndpoint = "/products";

        const [partnersRes, productsRes] = await Promise.all([
          axios.get(partnerEndpoint),
          axios.get(productEndpoint),
        ]);

        setPartners(partnersRes.data);
        setProducts(productsRes.data);

        const invoiceCount = 100; // Example for testing; replace with your actual logic
        const newInvoiceNo =
          invoiceType === "sales"
            ? `SI-${String(invoiceCount + 1).padStart(4, "0")}`
            : `PI-${String(invoiceCount + 1).padStart(4, "0")}`;

        setInvoiceData((prevState) => ({
          ...prevState,
          invoiceNo: newInvoiceNo,
        }));
        setIsLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setAlert({ type: "danger", message: `Error fetching data: ${error.message}` });
        setIsLoading(false);
      }
    };

    fetchData();
  }, [invoiceType]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoiceData((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleCreditTermChange = (e) => {
    const term = parseInt(e.target.value, 10) || 0;
    const newDueDate = new Date();
    newDueDate.setDate(newDueDate.getDate() + term);
    setInvoiceData((prevState) => ({
      ...prevState,
      creditTerm: term,
      dueDate: newDueDate.toISOString().split("T")[0],
    }));
  };

  const addProduct = () => {
    const selectedProduct = products.find((p) => p._id === productId);
    if (!selectedProduct) {
      setAlert({ type: "warning", message: "Product not found." });
      return;
    }

    const rate = parseFloat(selectedProduct.rate) || 0;
    const quantityInt = parseInt(quantity, 10) || 0;

    setInvoiceData((prevState) => ({
      ...prevState,
      invoiceItems: [
        ...prevState.invoiceItems,
        {
          productId,
          productName: selectedProduct.name,
          quantity: quantityInt,
          rate,
        },
      ],
      invoiceTotal: prevState.invoiceTotal + rate * quantityInt,
    }));

    setProductId("");
    setQuantity(1);
  };

  const handleRemoveInvoiceItem = (index) => {
    const items = [...invoiceData.invoiceItems];
    const removedItem = items.splice(index, 1); // إزالة العنصر بناءً على الفهرس
    setInvoiceData((prevState) => ({
      ...prevState,
      invoiceItems: items,
      invoiceTotal:
        prevState.invoiceTotal - (removedItem[0]?.rate * removedItem[0]?.quantity || 0),
    }));
  };

  const handleSubmit = async () => {
    if (invoiceData.partnerId === "") {
      setAlert({ type: "warning", message: "Please select a partner." });
      return;
    }
    if (invoiceData.invoiceItems.length === 0) {
      setAlert({
        type: "warning",
        message: "Please add at least one item to the invoice.",
      });
      return;
    }

    try {
      await axios.post("/invoices", invoiceData);
      setAlert({ type: "success", message: "Invoice created successfully!" });
      navigate(`/${invoiceType === "sales" ? "salesinvoices" : "purchaseinvoices"}`);
    } catch (error) {
      console.error("Error creating invoice:", error);
      setAlert({
        type: "danger",
        message: `Error creating invoice: ${
          error.response?.data?.message || "Unknown error"
        }`,
      });
    }
  };

  const handleCancel = () => {
    navigate(`/${invoiceType === "sales" ? "salesinvoices" : "purchaseinvoices"}`);
  };

  return isLoading ? (
    <Spinner />
  ) : (
    <div className="container mt-5">
      {alert.message && <Alert type={alert.type} message={alert.message} />}
      <div className="card shadow-lg border-0">
        <div className="card-header bg-light">
          <h3 className="text-center mb-0">Add Invoice</h3>
        </div>
        <div className="card-body">
          <div className="accordion" id="invoiceFormAccordion">
            <div className="accordion-item">
              <h2 className="accordion-header" id="addInvoiceHeading">
                <button
                  className="accordion-button"
                  type="button"
                  data-bs-toggle="collapse"
                  data-bs-target="#addInvoice"
                  aria-expanded="true"
                  aria-controls="addInvoice"
                >
                  Add Invoice Details
                </button>
              </h2>
              <div
                id="addInvoice"
                className="accordion-collapse collapse show"
                aria-labelledby="addInvoiceHeading"
              >
                <div className="accordion-body">
                  <form>
                    <div className="mb-3">
                      <label>Invoice No</label>
                      <input
                        className="form-control"
                        value={invoiceData.invoiceNo}
                        disabled
                      />
                    </div>
                    <div className="mb-3">
                      <label>Partner</label>
                      <select
                        className="form-control"
                        name="partnerId"
                        value={invoiceData.partnerId}
                        onChange={handleChange}
                      >
                        <option value="">Select Partner</option>
                        {partners.map((partner) => (
                          <option key={partner._id} value={partner._id}>
                            {partner.firstName} {partner.lastName}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div className="mb-3">
                      <label>Reference</label>
                      <input
                        className="form-control"
                        name="reference"
                        value={invoiceData.reference}
                        onChange={handleChange}
                      />
                    </div>
                    <div className="mb-3">
                      <label>Credit Term (days)</label>
                      <input
                        type="number"
                        className="form-control"
                        name="creditTerm"
                        value={invoiceData.creditTerm}
                        onChange={handleCreditTermChange}
                      />
                    </div>
                    <div className="mb-3">
                      <label>Due Date</label>
                      <input className="form-control" value={invoiceData.dueDate} disabled />
                    </div>
                    <div className="mb-3">
                      <label>Invoice Items</label>
                      <div className="d-flex">
                        <select
                          className="form-control me-2"
                          value={productId}
                          onChange={(e) => setProductId(e.target.value)}
                        >
                          <option value="">Select Product</option>
                          {products.map((product) => (
                            <option key={product._id} value={product._id}>
                              {product.name}
                            </option>
                          ))}
                        </select>
                        <input
                          type="number"
                          className="form-control me-2"
                          value={quantity}
                          onChange={(e) => setQuantity(parseInt(e.target.value, 10) || 1)}
                        />
                        <button className="btn btn-primary" onClick={addProduct} type="button">
                          Add Item
                        </button>
                      </div>
                    </div>
                    <table className="table">
                      <thead>
                        <tr>
                          <th>Product</th>
                          <th>Quantity</th>
                          <th>Rate</th>
                          <th>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {invoiceData.invoiceItems.map((item, index) => (
                          <tr key={index}>
                            <td>{item.productName}</td>
                            <td>{item.quantity}</td>
                            <td>{item.rate}</td>
                            <td>
                              <button
                                className="btn btn-danger btn-sm"
                                onClick={() => handleRemoveInvoiceItem(index)}
                              >
                                Remove
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                    <div className="d-flex justify-content-between">
                      <button className="btn btn-success" onClick={handleSubmit} type="button">
                        Submit Invoice
                      </button>
                      <button className="btn btn-secondary" onClick={handleCancel} type="button">
                        Cancel
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddInvoice;
