import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Alert from "./Alert";

const AddInvoice = ({ invoiceType }) => {
  const navigate = useNavigate();

  const [invoiceData, setInvoiceData] = useState({
    invoiceNo: "",
    partnerId: "",
    reference: "",
    invoiceDate: new Date().toISOString().split("T")[0],
    creditTerm: 0,
    dueDate: "",
    invoiceItems: [],
  });

  const [partners, setPartners] = useState([]);
  const [products, setProducts] = useState([]);
  const [productId, setProductId] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [alert, setAlert] = useState(null);

  // Fetch partners, products, and generate invoiceNo
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        // Fetch partners
        const partnersResponse = await fetch(
          `/partners?type=${invoiceType === "sales" ? "customer" : "vendor"}`
        );
        const partnersData = await partnersResponse.json();
        setPartners(partnersData);

        // Fetch existing invoices to generate invoiceNo
        const invoicesResponse = await fetch(`/invoices?type=${invoiceType}`);
        const invoicesData = await invoicesResponse.json();
        const nextInvoiceNumber = (invoicesData.length + 1).toString().padStart(4, "0");
        const invoicePrefix = invoiceType === "sales" ? "SI-" : "PI-";
        setInvoiceData((prev) => ({
          ...prev,
          invoiceNo: `${invoicePrefix}${nextInvoiceNumber}`,
        }));

        // Fetch products
        const productsResponse = await fetch("/products");
        const productsData = await productsResponse.json();
        setProducts(productsData);
      } catch (error) {
        setAlert({ type: "error", message: "Failed to load initial data." });
      }
    };

    fetchInitialData();
  }, [invoiceType]);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoiceData((prev) => ({ ...prev, [name]: value }));
  };

  // Calculate due date
  const handleCreditTermChange = (e) => {
    const creditTerm = parseInt(e.target.value, 10) || 0;
    const dueDate = new Date();
    dueDate.setDate(dueDate.getDate() + creditTerm);

    setInvoiceData((prev) => ({
      ...prev,
      creditTerm,
      dueDate: dueDate.toISOString().split("T")[0],
    }));
  };

  // Add product to invoice items
  const addProduct = () => {
    const selectedProduct = products.find((product) => product._id === productId);
    if (!selectedProduct) {
      setAlert({ type: "error", message: "Invalid product selection." });
      return;
    }

    const valueOfSupplies = quantity * selectedProduct.rate;
    const salesTax = (valueOfSupplies * selectedProduct.salesTax) / 100;
    const netAmount = valueOfSupplies + salesTax;

    setInvoiceData((prev) => ({
      ...prev,
      invoiceItems: [
        ...prev.invoiceItems,
        {
          productId,
          productName: selectedProduct.name,
          quantity,
          rate: selectedProduct.rate,
          valueOfSupplies,
          salesTax,
          netAmount,
        },
      ],
    }));

    setProductId("");
    setQuantity(1);
  };

  // Remove product from invoice items
  const handleRemoveInvoiceItem = (index) => {
    setInvoiceData((prev) => ({
      ...prev,
      invoiceItems: prev.invoiceItems.filter((_, i) => i !== index),
    }));
  };

  // Submit form
  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log("Data being sent:", JSON.stringify(invoiceData));

    if (!invoiceData.partnerId || invoiceData.invoiceItems.length === 0) {
      setAlert({ type: "error", message: "Please fill in all required fields and add items." });
      return;
    }

    try {
      const response = await fetch("/invoices", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(invoiceData),
      });

      if (!response.ok) {
        const errorText = await response.text(); // Handle text response on error
        console.error("Error Response Text:", errorText);
        setAlert({ type: "error", message: "Failed to add invoice: " + errorText });
        return;
      }

      const result = await response.json();
      console.log("Success:", result);
      setAlert({ type: "success", message: "Invoice added successfully!" });
      navigate(`/${invoiceType}invoices`);
    } catch (error) {
      setAlert({ type: "error", message: "An error occurred while submitting the invoice." });
      console.error("Error submitting invoice:", error);
    }
  };

  // Cancel form
  const handleCancel = () => navigate(`/${invoiceType}invoices`);

  return (
    <div className="container mt-5">
      {alert && <Alert type={alert.type} message={alert.message} className="mb-4" />}
      <div className="card shadow-lg border-0">
        <div className="card-header bg-gradient-primary text-white">
          <h3 className="text-center mb-0">Add {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice</h3>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <h5>Invoice Details</h5>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label>Invoice Number</label>
                <input type="text" className="form-control" value={invoiceData.invoiceNo} readOnly />
              </div>
              <div className="col-md-6 mb-3">
                <label>Customer/Vendor</label>
                <select
                  className="form-select"
                  name="partnerId"
                  value={invoiceData.partnerId}
                  onChange={handleChange}
                >
                  <option value="">Select</option>
                  {partners.map((partner) => (
                    <option key={partner._id} value={partner._id}>
                      {partner.firstName} {partner.lastName}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="row">
              <div className="col-md-6 mb-3">
                <label>Credit Term (days)</label>
                <input
                  type="number"
                  className="form-control"
                  value={invoiceData.creditTerm}
                  onChange={handleCreditTermChange}
                />
              </div>
              <div className="col-md-6 mb-3">
                <label>Due Date</label>
                <input type="date" className="form-control" value={invoiceData.dueDate} readOnly />
              </div>
            </div>

            <h5>Invoice Items</h5>
            <div className="row">
              <div className="col-md-6 mb-3">
                <select
                  className="form-select"
                  value={productId}
                  onChange={(e) => setProductId(e.target.value)}
                >
                  <option value="">Select Product</option>
                  {products.map((product) => (
                    <option key={product._id} value={product._id}>
                      {product.name}
                    </option>
                  ))}
                </select>
              </div>
              <div className="col-md-3 mb-3">
                <input
                  type="number"
                  className="form-control"
                  placeholder="Quantity"
                  value={quantity}
                  onChange={(e) => setQuantity(Number(e.target.value))}
                />
              </div>
              <div className="col-md-3">
                <button type="button" className="btn btn-success" onClick={addProduct}>
                  Add Product
                </button>
              </div>
            </div>

            <table className="table">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>Quantity</th>
                  <th>Rate</th>
                  <th>Value</th>
                  <th>Tax</th>
                  <th>Net Amount</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {invoiceData.invoiceItems.map((item, index) => (
                  <tr key={index}>
                    <td>{item.productName}</td>
                    <td>{item.quantity}</td>
                    <td>${item.rate.toFixed(2)}</td>
                    <td>${item.valueOfSupplies.toFixed(2)}</td>
                    <td>${item.salesTax.toFixed(2)}</td>
                    <td>${item.netAmount.toFixed(2)}</td>
                    <td>
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => handleRemoveInvoiceItem(index)}
                      >
                        Remove
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            <div className="text-center mt-4">
              <button type="submit" className="btn btn-success me-3">
                Submit
              </button>
              <button type="button" className="btn btn-danger" onClick={handleCancel}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddInvoice;
