import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import Spinner from "./Spinner"; // Import Spinner component
import Alert from "./Alert"; // Import Alert component

const Products = () => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true); // Loading state
  const [alertType, setAlertType] = useState(null); // Alert type (success, danger, etc.)
  const [alertMessage, setAlertMessage] = useState(""); // Alert message
  const [products, setProducts] = useState([]); // List of products

  useEffect(() => {
    // Fetch all products
    const fetchProducts = async () => {
      try {
        const response = await axios.get("/products");
        if (response.status === 200) {
          if (response.data.length === 0) {
            setAlertType("warning");
            setAlertMessage("No products found.");
          } else {
            setProducts(response.data);
          }
        } else {
          setAlertType("danger");
          setAlertMessage(
            `Failed to retrieve products: ${response.status} ${response.statusText}`
          );
        }
        setIsLoading(false);
      } catch (error) {
        setAlertType("danger");
        setAlertMessage(`Failed to retrieve products: ${error.message}`);
        setIsLoading(false); // Stop loading on error
      }
    };

    fetchProducts();
  }, []);

  const handleEditProduct = (product) => {
    // Navigate to the EditProduct component with the product ID as a parameter
    const editProductUrl = `edit?id=${product._id}`;
    navigate(editProductUrl);
  };

  return (
    <div>
      {isLoading ? (
        <Spinner /> // Show Spinner while loading
      ) : (
        <>
          <h2>Products</h2>
          <Link to="add" className="btn btn-outline-secondary mt-3 mb-4">
            Add Product
          </Link>
          {alertMessage ? (
            <Alert type={alertType} message={alertMessage} /> // Show alert if there is a message
          ) : (
            <table className="table table-striped table-hover">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Core Company</th>
                  <th>Rate</th>
                  <th>Status</th>
                  <th>Tax Exempted</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {products.map((product) => (
                  <tr key={product._id}>
                    <td>{product.name}</td>
                    <td>{product.coreCompany}</td>
                    <td>{product.rate}</td>
                    <td>{product.status}</td>
                    <td>{product.taxExempted}</td>
                    <td>
                      <button
                        className="btn btn-sm btn-outline-success me-2"
                        onClick={() => handleEditProduct(product)}
                      >
                        <i className="bi bi-pencil-square"></i> Edit
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </>
      )}
    </div>
  );
};

export default Products;
