import React, { useState, useEffect } from "react";
import NavBar from "./NavBar";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Spinner from "./Spinner";
import Alert from "./Alert";

const AddInvoice = ({ invoiceType }) => {
  const initialState = {
    type: invoiceType,
    invoiceDate: new Date().toISOString().split("T")[0],
    dueDate: new Date().toISOString().split("T")[0],
    invoiceNo: "",
    partnerId: "",
    creditTerm: 0,
    reference: "",
    invoiceTotal: 0,
    invoiceItems: [],
  };

  const [invoiceData, setInvoiceData] = useState(initialState);
  const [partners, setPartners] = useState([]);
  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [alert, setAlert] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch partners based on invoiceType
        const partnerResponse = await axios.get(
          `/partners?type=${invoiceType === "sales" ? "customer" : "vendor"}`
        );

        if (partnerResponse.status === 200) {
          setPartners(partnerResponse.data);
        }

        // Fetch invoice number
        const invoiceResponse = await axios.get(`/invoices?type=${invoiceType}`);
        if (invoiceResponse.status === 200) {
          const totalInvoices = invoiceResponse.data.length;
          const nextInvoiceNumber = totalInvoices + 1;
          const paddedNumber = nextInvoiceNumber.toString().padStart(4, "0");
          const invoicePrefix = invoiceType === "sales" ? "SI-" : "PI-";
          setInvoiceData((prevData) => ({
            ...prevData,
            invoiceNo: `${invoicePrefix}${paddedNumber}`,
          }));
        }

        // Fetch products
        const productResponse = await axios.get("/products");
        if (productResponse.status === 200) {
          setProducts(productResponse.data);
        }

        setIsLoading(false);
      } catch (error) {
        setAlert({ type: "danger", message: "Failed to fetch data." });
        setIsLoading(false);
      }
    };

    fetchData();
  }, [invoiceType]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoiceData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/invoices", invoiceData);
      if (response.status === 201) {
        setAlert({ type: "success", message: "Invoice created successfully!" });
        navigate(`/invoices/${invoiceType}`);
      } else {
        setAlert({ type: "danger", message: "Failed to create invoice." });
      }
    } catch (error) {
      setAlert({ type: "danger", message: "An error occurred while creating invoice." });
    }
  };

  const handleCancel = () => {
    navigate(`/invoices/${invoiceType}`);
  };

  const addProduct = (productId, quantity) => {
    const selectedProduct = products.find((product) => product._id === productId);
    if (selectedProduct) {
      const valueOfSupplies = quantity * selectedProduct.rate;
      const salesTax = (valueOfSupplies * selectedProduct.salesTax) / 100;
      const netAmount = valueOfSupplies + salesTax;

      setInvoiceData((prevData) => ({
        ...prevData,
        invoiceItems: [
          ...prevData.invoiceItems,
          { productId, quantity, valueOfSupplies, salesTax, netAmount },
        ],
        invoiceTotal: prevData.invoiceTotal + netAmount,
      }));
    }
  };

  const handleRemoveInvoiceItem = (index) => {
    const itemToRemove = invoiceItems[index];
    setInvoiceData((prevData) => ({
      ...prevData,
      invoiceItems: prevData.invoiceItems.filter((_, i) => i !== index),
      invoiceTotal: prevData.invoiceTotal - itemToRemove.netAmount,
    }));
  };

  const handleCreditTermChange = (e) => {
    const creditTerm = parseInt(e.target.value, 10);
    const newDueDate = new Date();
    newDueDate.setDate(newDueDate.getDate() + creditTerm);
    setInvoiceData((prevData) => ({
      ...prevData,
      creditTerm,
      dueDate: newDueDate.toISOString().split("T")[0],
    }));
  };

  if (isLoading) return <Spinner />;

  return (
    <div>
      <NavBar />
      <div className="container mt-4">
        {alert && <Alert type={alert.type} message={alert.message} />}
        <div className="card shadow-lg border-0">
          <div className="card-header bg-gradient-primary text-white">
            <h3 className="text-center mb-0">Add {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice</h3>
          </div>
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label htmlFor="invoiceNo" className="form-label">Invoice No</label>
                  <input type="text" className="form-control" id="invoiceNo" value={invoiceNo} disabled />
                </div>
                <div className="col-md-6 mb-3">
                  <label htmlFor="partnerId" className="form-label">Partner</label>
                  <select
                    className="form-select"
                    id="partnerId"
                    name="partnerId"
                    value={partnerId}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Partner</option>
                    {partners.map((partner) => (
                      <option key={partner._id} value={partner._id}>
                        {partner.name}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label htmlFor="creditTerm" className="form-label">Credit Term (Days)</label>
                  <input
                    type="number"
                    className="form-control"
                    id="creditTerm"
                    name="creditTerm"
                    value={creditTerm}
                    onChange={handleCreditTermChange}
                    required
                  />
                </div>
                <div className="col-md-6 mb-3">
                  <label htmlFor="dueDate" className="form-label">Due Date</label>
                  <input type="date" className="form-control" id="dueDate" value={dueDate} disabled />
                </div>
              </div>
              {/* Add invoice items */}
              <div className="mb-3">
                <h5>Invoice Items</h5>
                {invoiceItems.map((item, index) => (
                  <div key={index} className="row align-items-center mb-2">
                    <div className="col-md-4">{item.productId}</div>
                    <div className="col-md-2">{item.quantity}</div>
                    <div className="col-md-3">{item.netAmount}</div>
                    <div className="col-md-3">
                      <button
                        type="button"
                        className="btn btn-danger btn-sm"
                        onClick={() => handleRemoveInvoiceItem(index)}
                      >
                        Remove
                      </button>
                    </div>
                  </div>
                ))}
              </div>
              <div className="text-center mt-4">
                <button type="submit" className="btn btn-success me-3">
                  Submit
                </button>
                <button type="button" className="btn btn-danger" onClick={handleCancel}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddInvoice;
