import React, { useState, useEffect } from "react";
import axios from "axios";
import LedgerReport from "./LedgerReport";
import Alert from "./Alert";

const Ledger = () => {
  const [formData, setFormData] = useState({
    partnerId: "",
    startDate: "",
    endDate: "",
  });
  const [partners, setPartners] = useState([]);
  const [ledgerItems, setLedgerItems] = useState([]);
  const [partner, setPartner] = useState(null);
  const [showReport, setShowReport] = useState(false);
  const [alert, setAlert] = useState(null);

  const { partnerId, startDate, endDate } = formData;

  useEffect(() => {
    const fetchPartners = async () => {
      try {
        const response = await axios.get("/partners");
        setPartners(response.data);
      } catch (error) {
        setAlert({ type: "danger", message: "Failed to fetch partners." });
      }
    };
    fetchPartners();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.get(
        `/ledger?partnerId=${partnerId}&startDate=${startDate}&endDate=${endDate}`
      );
      setLedgerItems(response.data.ledgerItems || []);
      setPartner(response.data.partner || null);
      setShowReport(true);
    } catch (error) {
      setAlert({ type: "danger", message: "Failed to generate ledger report." });
    }
  };

  return (
    <div className="container mt-5">
      {alert && <Alert type={alert.type} message={alert.message} />}
      <div className="card shadow-lg border-0">
        <div className="card-header bg-gradient-primary text-white">
          <h3 className="text-center mb-0">Ledger</h3>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label htmlFor="partnerId" className="form-label">
                  Partner
                </label>
                <select
                  id="partnerId"
                  name="partnerId"
                  className="form-select"
                  value={partnerId}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select Partner</option>
                  {partners.map((partner) => (
                    <option key={partner._id} value={partner._id}>
                      {partner.firstName} {partner.lastName}
                    </option>
                  ))}
                </select>
              </div>
              <div className="col-md-3 mb-3">
                <label htmlFor="startDate" className="form-label">
                  Start Date
                </label>
                <input
                  type="date"
                  id="startDate"
                  name="startDate"
                  className="form-control"
                  value={startDate}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-3 mb-3">
                <label htmlFor="endDate" className="form-label">
                  End Date
                </label>
                <input
                  type="date"
                  id="endDate"
                  name="endDate"
                  className="form-control"
                  value={endDate}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="text-center mt-4">
              <button type="submit" className="btn btn-success me-2">
                Generate
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => setShowReport(false)}
              >
                Clear
              </button>
            </div>
          </form>
        </div>
      </div>

      {showReport && (
        <LedgerReport
          partner={partner}
          ledgerItems={ledgerItems}
          startDate={startDate}
          endDate={endDate}
        />
      )}
    </div>
  );
};

export default Ledger;
