import React, { useState, useEffect } from "react";
import NavBar from "./NavBar";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Spinner from "./Spinner";
import Alert from "./Alert";

const AddInvoice = ({ invoiceType }) => {
  const initialState = {
    type: invoiceType,
    invoiceDate: new Date().toISOString().split("T")[0],
    dueDate: new Date().toISOString().split("T")[0],
    invoiceNo: "",
    partnerId: "",
    creditTerm: 0,
    reference: "",
    invoiceTotal: 0,
    invoiceItems: [],
  };

  const [invoiceData, setInvoiceData] = useState(initialState);
  const [partners, setPartners] = useState([]);
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState(null);

  const navigate = useNavigate();

  const fetchInitialData = async () => {
    setLoading(true);
    try {
      const partnerType = invoiceType === "sales" ? "customers" : "vendors";

      // Fetch partners with query parameters
      const partnersResponse = await axios.get(`/partners`, {
        params: { type: partnerType },
      });

      console.log("Partners Response:", partnersResponse.data); // Debugging

      // Validate and set partner data
      if (Array.isArray(partnersResponse.data) && partnersResponse.data.length > 0) {
        setPartners(partnersResponse.data);
      } else {
        setAlert({ type: "warning", message: "No partners found." });
      }

      // Fetch invoices to generate the next invoice number
      const invoicesResponse = await axios.get(`/invoices?type=${invoiceType}`);
      const invoiceCount = invoicesResponse.data.length + 1;
      const prefix = invoiceType === "sales" ? "SI-" : "PI-";
      const invoiceNo = `${prefix}${String(invoiceCount).padStart(4, "0")}`;
      setInvoiceData((prev) => ({ ...prev, invoiceNo }));
    } catch (error) {
      console.error("Error fetching data:", error);
      setAlert({ type: "danger", message: "Failed to fetch initial data." });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchInitialData();
  }, [invoiceType]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoiceData((prev) => ({ ...prev, [name]: value }));
  };

  const handleCreditTermChange = (e) => {
    const creditTerm = parseInt(e.target.value, 10) || 0;
    const dueDate = new Date();
    dueDate.setDate(dueDate.getDate() + creditTerm);
    setInvoiceData((prev) => ({
      ...prev,
      creditTerm,
      dueDate: dueDate.toISOString().split("T")[0],
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post("/invoices", invoiceData);
      setAlert({ type: "success", message: "Invoice created successfully!" });
      navigate(`/invoices/${invoiceType}`);
    } catch (error) {
      console.error("Error creating invoice:", error);
      setAlert({ type: "danger", message: "Failed to create the invoice." });
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    navigate(`/invoices/${invoiceType}`);
  };

  return (
    <div>
      <NavBar />
      <div className="container mt-4">
        {loading && <Spinner />}
        {alert && <Alert type={alert.type} message={alert.message} />}
        <form onSubmit={handleSubmit}>
          <h3>Add {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice</h3>

          <div className="form-group">
            <label>Invoice No</label>
            <input
              type="text"
              className="form-control"
              value={invoiceData.invoiceNo}
              readOnly
            />
          </div>

          <div className="form-group">
            <label>Partner</label>
            <select
              name="partnerId"
              className="form-control"
              value={invoiceData.partnerId}
              onChange={handleChange}
            >
              <option value="">Select Partner</option>
              {partners.map((partner) => (
                <option key={partner.id} value={partner.id}>
                  {partner.name}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Reference</label>
            <input
              type="text"
              name="reference"
              className="form-control"
              value={invoiceData.reference}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>Due Date</label>
            <input
              type="date"
              className="form-control"
              name="dueDate"
              value={invoiceData.dueDate}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>Credit Term</label>
            <input
              type="number"
              className="form-control"
              name="creditTerm"
              value={invoiceData.creditTerm}
              onChange={handleCreditTermChange}
            />
          </div>

          <button type="submit" className="btn btn-success">
            Submit
          </button>
          <button type="button" className="btn btn-secondary ml-2" onClick={handleCancel}>
            Cancel
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddInvoice;
