import React, { useState, useEffect } from "react";

const LedgerReport = ({ partner, ledgerItems, startDate, endDate }) => {
  // States to manage totals and balances
  const [totalDebit, setTotalDebit] = useState(0);
  const [totalCredit, setTotalCredit] = useState(0);
  const [runningBalance, setRunningBalance] = useState(0);
  const [updatedLedgerItems, setUpdatedLedgerItems] = useState([]);

  // Calculate balances and totals when the component mounts or props change
  useEffect(() => {
    let debitTotal = 0;
    let creditTotal = 0;
    let currentBalance = parseFloat(partner.openingBalance);

    const updatedItems = ledgerItems.map((item) => {
      const debit = parseFloat(item.debit);
      const credit = parseFloat(item.credit);
      const balance = (currentBalance + debit - credit).toFixed(2);

      debitTotal += debit;
      creditTotal += credit;
      currentBalance = parseFloat(balance);

      return { ...item, balance };
    });

    // Include opening balance in totals
    if (partner.openingBalance >= 0) {
      debitTotal += parseFloat(partner.openingBalance);
    } else {
      creditTotal += Math.abs(parseFloat(partner.openingBalance));
    }

    setTotalDebit(debitTotal);
    setTotalCredit(creditTotal);
    setUpdatedLedgerItems(updatedItems);
    setRunningBalance(currentBalance);
  }, [partner, ledgerItems, startDate, endDate]);

  return (
    <div className="container mt-4">
      <div className="card shadow-lg border-0">
        {/* Header Section */}
        <div className="card-body">
          <div className="row align-items-start">
            <div className="col">
              <img
                src="/logos/logo.png"
                alt="Company Logo"
                className="img-fluid"
                style={{ maxWidth: "100px" }}
              />
            </div>
            <div className="col text-end">
              <h1>GENERAL LEDGER</h1>
            </div>
          </div>

          {/* Partner Information Section */}
          <div className="row mt-3 mb-5">
            <hr className="mb-0" />
            <div className="col">
              <strong>Full Name:</strong> {partner.firstName} {partner.lastName}
              <br />
              <strong>Address:</strong> {partner.address}
              <br />
              <strong>Contact No.:</strong> {partner.contactNo}
            </div>
            <div className="col text-end">
              <strong>Status:</strong> {partner.status}
              <br />
              <strong>Statement From:</strong> {startDate}
              <br />
              <strong>Statement To:</strong> {endDate}
            </div>
            <hr />
          </div>

          {/* Table Section */}
          <div className="table-responsive">
            <table className="table table-bordered text-center table-sm">
              <thead>
                <tr>
                  <th className="bg-light">Date</th>
                  <th className="bg-light">Instrument No.</th>
                  <th className="bg-light">Reference</th>
                  <th className="bg-light">Description</th>
                  <th className="bg-light">Quantity</th>
                  <th className="bg-light">Debit</th>
                  <th className="bg-light">Credit</th>
                  <th className="bg-light">Balance</th>
                </tr>
              </thead>
              <tbody>
                {/* Opening Balance */}
                <tr>
                  <td>{startDate}</td>
                  <td></td>
                  <td></td>
                  <td>Opening Balance</td>
                  <td></td>
                  <td className="text-end">
                    {parseFloat(partner.openingBalance) >= 0
                      ? `$${parseFloat(partner.openingBalance).toLocaleString()}`
                      : ""}
                  </td>
                  <td className="text-end">
                    {parseFloat(partner.openingBalance) < 0
                      ? `$${Math.abs(parseFloat(partner.openingBalance)).toLocaleString()}`
                      : ""}
                  </td>
                  <td className="text-end">
                    {partner.openingBalance >= 0
                      ? `$${parseFloat(partner.openingBalance).toLocaleString()}`
                      : `($${Math.abs(parseFloat(partner.openingBalance)).toLocaleString()})`}
                  </td>
                </tr>

                {/* Ledger Items */}
                {updatedLedgerItems.map((item, index) => (
                  <tr key={index}>
                    <td>{item.date}</td>
                    <td>{item.instrumentNo}</td>
                    <td>{item.reference}</td>
                    <td>{item.description}</td>
                    <td>
                      {item.quantity === "0"
                        ? ""
                        : parseFloat(item.quantity).toLocaleString()}
                    </td>
                    <td className="text-end">
                      ${parseFloat(item.debit).toLocaleString()}
                    </td>
                    <td className="text-end">
                      ${parseFloat(item.credit).toLocaleString()}
                    </td>
                    <td className="text-end">
                      {item.balance >= 0
                        ? `$${parseFloat(item.balance).toLocaleString()}`
                        : `($${Math.abs(parseFloat(item.balance)).toLocaleString()})`}
                    </td>
                  </tr>
                ))}
              </tbody>

              {/* Totals */}
              <tfoot>
                <tr className="fw-bold">
                  <td colSpan={4} className="text-start bg-light">
                    Total:
                  </td>
                  <td className="bg-light">
                    {ledgerItems
                      .reduce((total, item) => total + parseFloat(item.quantity), 0)
                      .toLocaleString()}
                  </td>
                  <td className="text-end bg-light">
                    ${parseFloat(totalDebit).toLocaleString()}
                  </td>
                  <td className="text-end bg-light">
                    ${parseFloat(totalCredit).toLocaleString()}
                  </td>
                  <td className="text-end bg-light">
                    {runningBalance >= 0
                      ? `$${parseFloat(runningBalance).toLocaleString()}`
                      : `($${Math.abs(parseFloat(runningBalance)).toLocaleString()})`}
                  </td>
                </tr>
              </tfoot>
            </table>
          </div>

          {/* Back Button */}
          <div className="text-center mt-4">
            <a href="/ledger" className="btn btn-danger">
              <i className="fa fa-arrow-left"></i> Back
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LedgerReport;
