import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Alert from "./Alert";
import axios from "axios";

const AddInvoice = ({ invoiceType }) => {
  const initialState = {
    type: invoiceType,
    invoiceDate: new Date().toISOString().split("T")[0],
    dueDate: "",
    invoiceNo: "",
    partnerId: "",
    creditTerm: 0,
    reference: "",
    invoiceTotal: 0,
    invoiceItems: [],
  };

  const [invoiceData, setInvoiceData] = useState(initialState);
  const [partners, setPartners] = useState([]);
  const [products, setProducts] = useState([]);
  const [productId, setProductId] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [alert, setAlert] = useState(null);
  const navigate = useNavigate();

  // Fetch data (partners, products, and generate invoice number)
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [partnersRes, productsRes, invoicesRes] = await Promise.all([
          axios.get(
            `/partners?type=${
              invoiceType === "sales" ? "customer" : "vendor"
            }&status=Active`
          ),
          axios.get("/products?status=Active"),
          axios.get(`/invoices?type=${invoiceType}`),
        ]);

        // Set partners
        setPartners(partnersRes.data);

        // Format products: ensure rate is a number
        const formattedProducts = productsRes.data.map((product) => ({
          ...product,
          rate: Number(product.rate) || 0, // Ensure rate is a number
        }));
        setProducts(formattedProducts);

        // Generate next invoice number
        const nextInvoiceNumber = `${
          invoiceType === "sales" ? "SI" : "PI"
        }-${String(invoicesRes.data.length + 1).padStart(4, "0")}`;

        setInvoiceData((prev) => ({ ...prev, invoiceNo: nextInvoiceNumber }));
      } catch (error) {
        setAlert({
          type: "error",
          message: "Failed to load initial data. Please try again.",
        });
      }
    };

    fetchData();
  }, [invoiceType]);

  // Handle input changes for invoice data
  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoiceData((prev) => ({ ...prev, [name]: value }));
  };

  // Handle credit term change and calculate due date
  const handleCreditTermChange = (e) => {
    const creditTerm = parseInt(e.target.value, 10) || 0;
    const dueDate = new Date();
    dueDate.setDate(dueDate.getDate() + creditTerm);

    setInvoiceData((prev) => ({
      ...prev,
      creditTerm,
      dueDate: dueDate.toISOString().split("T")[0],
    }));
  };

  // Add product to invoice items
  const addProduct = () => {
    const selectedProduct = products.find((product) => product._id === productId);
    if (selectedProduct && quantity > 0) {
      const rate = Number(selectedProduct.rate) || 0; // Ensure rate is a number
      const valueOfSupplies = quantity * rate;
      const salesTax = (valueOfSupplies * selectedProduct.salesTax) / 100;
      const netAmount = valueOfSupplies + salesTax;

      const newItem = {
        productId,
        productName: selectedProduct.name,
        quantity,
        rate,
        valueOfSupplies,
        salesTax,
        netAmount,
      };

      setInvoiceData((prev) => ({
        ...prev,
        invoiceTotal: prev.invoiceTotal + netAmount,
        invoiceItems: [...prev.invoiceItems, newItem],
      }));

      setProductId("");
      setQuantity(1);
    } else {
      setAlert({ type: "error", message: "Please select a product and enter quantity." });
    }
  };

  // Remove product from invoice items
  const handleRemoveInvoiceItem = (index) => {
    const updatedItems = invoiceData.invoiceItems.filter((_, i) => i !== index);
    const newTotal = updatedItems.reduce((sum, item) => sum + item.netAmount, 0);

    setInvoiceData((prev) => ({
      ...prev,
      invoiceItems: updatedItems,
      invoiceTotal: newTotal,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    console.log("Sending Invoice Data:", invoiceData); // طباعة البيانات للتحقق
  
    try {
      const response = await axios.post("/invoices", invoiceData);
  
      if (response.status === 201) {
        setAlert({ type: "success", message: "Invoice added successfully!" });
        navigate(`/${invoiceType}invoices`);
      }
    } catch (error) {
      console.error("Error response:", error.response);
      setAlert({
        type: "error",
        message:
          error.response?.data?.message || "Failed to add invoice. Please try again.",
      });
    }
  };
  

  const handleCancel = () => {
    navigate(`/${invoiceType}invoices`);
  };

  return (
    <div className="container mt-5">
      {alert && <Alert type={alert.type} message={alert.message} />}
      <div className="card shadow-lg border-0">
        <div className="card-header bg-gradient-primary text-white">
          <h3 className="text-center mb-0">
            Add {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice
          </h3>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="accordion" id="invoiceFormAccordion">
              {/* Invoice Details Section */}
              <div className="accordion-item">
                <h2 className="accordion-header" id="invoiceDetailsHeading">
                  <button
                    className="accordion-button"
                    type="button"
                    data-bs-toggle="collapse"
                    data-bs-target="#invoiceDetails"
                    aria-expanded="true"
                    aria-controls="invoiceDetails"
                  >
                    Invoice Details
                  </button>
                </h2>
                <div
                  id="invoiceDetails"
                  className="accordion-collapse collapse show"
                  aria-labelledby="invoiceDetailsHeading"
                >
                  <div className="accordion-body">
                    <div className="row">
                      <div className="col-md-6 mb-3">
                        <label>Invoice No</label>
                        <input type="text" className="form-control" value={invoiceData.invoiceNo} readOnly />
                      </div>
                      <div className="col-md-6 mb-3">
                        <label>{invoiceType === "sales" ? "Customer" : "Vendor"}</label>
                        <select
                          className="form-select"
                          name="partnerId"
                          value={invoiceData.partnerId}
                          onChange={handleChange}
                          required
                        >
                          <option value="">Select</option>
                          {partners.map((partner) => (
                            <option key={partner._id} value={partner._id}>
                              {partner.firstName} {partner.lastName}
                            </option>
                          ))}
                        </select>
                      </div>
                      <div className="col-md-6 mb-3">
                        <label>Credit Term (days)</label>
                        <input
                          type="number"
                          className="form-control"
                          value={invoiceData.creditTerm}
                          onChange={handleCreditTermChange}
                        />
                      </div>
                      <div className="col-md-6 mb-3">
                        <label>Due Date</label>
                        <input type="date" className="form-control" value={invoiceData.dueDate} readOnly />
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Invoice Items Section */}
              <div className="accordion-item">
                <h2 className="accordion-header" id="invoiceItemsHeading">
                  <button
                    className="accordion-button collapsed"
                    type="button"
                    data-bs-toggle="collapse"
                    data-bs-target="#invoiceItems"
                    aria-expanded="false"
                    aria-controls="invoiceItems"
                  >
                    Invoice Items
                  </button>
                </h2>
                <div id="invoiceItems" className="accordion-collapse collapse" aria-labelledby="invoiceItemsHeading">
                  <div className="accordion-body">
                    <div className="row">
                      <div className="col-md-4 mb-3">
                        <label>Product</label>
                        <select
                          className="form-select"
                          value={productId}
                          onChange={(e) => setProductId(e.target.value)}
                        >
                          <option value="">Select Product</option>
                          {products.map((product) => (
                            <option key={product._id} value={product._id}>
                              {product.name}
                            </option>
                          ))}
                        </select>
                      </div>
                      <div className="col-md-3 mb-3">
                        <label>Quantity</label>
                        <input
                          type="number"
                          className="form-control"
                          value={quantity}
                          onChange={(e) => setQuantity(Number(e.target.value))}
                          min="1"
                        />
                      </div>
                      <div className="col-md-3 mt-4">
                        <button type="button" className="btn btn-outline-success" onClick={addProduct}>
                          Add Product
                        </button>
                      </div>
                    </div>

                    {/* Invoice Items Table */}
                    {invoiceData.invoiceItems.length > 0 && (
                      <table className="table mt-3">
                        <thead>
                          <tr>
                            <th>Product</th>
                            <th>Quantity</th>
                            <th>Rate</th>
                            <th>Value</th>
                            <th>Tax</th>
                            <th>Net Amount</th>
                            <th>Actions</th>
                          </tr>
                        </thead>
                        <tbody>
                          {invoiceData.invoiceItems.map((item, index) => (
                            <tr key={index}>
                              <td>{item.productName}</td>
                              <td>{item.quantity}</td>
                              <td>${item.rate.toFixed(2)}</td>
                              <td>${item.valueOfSupplies.toFixed(2)}</td>
                              <td>${item.salesTax.toFixed(2)}</td>
                              <td>${item.netAmount.toFixed(2)}</td>
                              <td>
                                <button
                                  className="btn btn-sm btn-danger"
                                  onClick={() => handleRemoveInvoiceItem(index)}
                                >
                                  Remove
                                </button>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    )}
                  </div>
                </div>
              </div>
            </div>

            <div className="text-center mt-4">
              <button type="submit" className="btn btn-success me-3">
                Submit
              </button>
              <button type="button" className="btn btn-danger" onClick={handleCancel}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddInvoice;
