import React, { useState, useEffect } from "react";
import NavBar from "./NavBar";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Spinner from "./Spinner";
import Alert from "./Alert";

const AddInvoice = ({ invoiceType }) => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const [alertType, setAlertType] = useState(null);
  const [alertMessage, setAlertMessage] = useState("");
  const [partners, setPartners] = useState([]);
  const [products, setProducts] = useState([]);
  const [page, setPage] = useState(1);
  const [productId, setProductId] = useState("");
  const [quantity, setQuantity] = useState(0);

  const initialState = {
    type: invoiceType,
    invoiceDate: new Date().toISOString().split("T")[0],
    dueDate: new Date().toISOString().split("T")[0],
    invoiceNo: "",
    partnerId: "",
    creditTerm: 0,
    reference: "",
    invoiceTotal: 0,
    invoiceItems: [],
  };

  const [invoiceData, setInvoiceData] = useState(initialState);

  const fetchData = async () => {
    try {
      const [invoiceNumberResponse, partnersResponse, productsResponse] =
        await Promise.all([
          axios.get(`/invoices?type=${invoiceType}`),
          axios.get(
            `/partners?type=${
              invoiceType === "sales" ? "customer" : "vendor"
            }&status=Active`
          ),
          axios.get(`/products?status=Active`),
        ]);

      if (invoiceNumberResponse.status === 200) {
        const totalInvoices = invoiceNumberResponse.data.length;
        const nextInvoiceNumber = `${
          invoiceType === "sales" ? "SI" : "PI"
        }-${String(totalInvoices + 1).padStart(4, "0")}`;
        setInvoiceData((prevData) => ({
          ...prevData,
          invoiceNo: nextInvoiceNumber,
        }));
      }

      if (partnersResponse.status === 200) {
        setPartners(partnersResponse.data);
      }

      if (productsResponse.status === 200) {
        setProducts(productsResponse.data);
      }

      setIsLoading(false);
    } catch (error) {
      setAlertType("danger");
      setAlertMessage(`Failed to fetch data: ${error.message}`);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [invoiceType]);

  const addProduct = () => {
    if (productId && quantity) {
      const selectedProduct = products.find(
        (product) => product._id === productId
      );

      if (selectedProduct) {
        const valueOfSupplies = quantity * selectedProduct.rate;
        const salesTax = (valueOfSupplies * selectedProduct.salesTax) / 100;
        const netAmount = valueOfSupplies + salesTax;

        const newItem = {
          productName: selectedProduct.name,
          quantity,
          rate: selectedProduct.rate,
          valueOfSupplies: valueOfSupplies.toFixed(2),
          salesTax: salesTax.toFixed(2),
          netAmount: netAmount.toFixed(2),
        };

        setInvoiceData((prevData) => ({
          ...prevData,
          invoiceTotal: prevData.invoiceTotal + netAmount,
          invoiceItems: [...prevData.invoiceItems, newItem],
        }));

        setProductId("");
        setQuantity(0);
      } else {
        alert("Selected product not found.");
      }
    }
  };

  const handleRemoveInvoiceItem = (itemToRemove) => {
    const updatedInvoiceItems = invoiceData.invoiceItems.filter(
      (item) => item !== itemToRemove
    );

    const newInvoiceTotal = updatedInvoiceItems.reduce(
      (total, item) => total + parseFloat(item.netAmount),
      0
    );

    setInvoiceData((prevData) => ({
      ...prevData,
      invoiceTotal: newInvoiceTotal,
      invoiceItems: updatedInvoiceItems,
    }));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoiceData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleCreditTermChange = (e) => {
    const newCreditTerm = e.target.value === "" ? null : parseInt(e.target.value);
    const currentDate = new Date();
    const invoiceDate = currentDate.toISOString().split("T")[0];
    const newDate = new Date(
      currentDate.getTime() + newCreditTerm * 24 * 60 * 60 * 1000
    );
    const dueDate = newDate.toISOString().split("T")[0];

    setInvoiceData((prevData) => ({
      ...prevData,
      creditTerm: newCreditTerm,
      invoiceDate,
      dueDate,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/invoices", invoiceData);
      if (response.status === 201) {
        setAlertType("success");
        setAlertMessage("Invoice added successfully.");
        setInvoiceData(initialState);
        setPage(1);
      }
    } catch (error) {
      setAlertType("danger");
      setAlertMessage(`Failed to add invoice: ${error.message}`);
    }
  };

  const handleCancel = () => {
    navigate(`/${invoiceType}invoices`);
  };

  const handleNavigation = () => {
    setPage(page === 1 ? 2 : 1);
  };

  return (
    <div>
      <NavBar />
      <div className="container mt-4">
        {isLoading ? (
          <Spinner />
        ) : (
          <>
            <h2>Create {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice</h2>
            <form onSubmit={handleSubmit}>
              {page === 1 ? (
                <div>
                  <h3>Invoice Details</h3>
                  <div className="mb-3">
                    <label htmlFor="partnerId" className="form-label">
                      {invoiceType === "sales" ? "Customer" : "Vendor"} Name
                    </label>
                    <select
                      id="partnerId"
                      name="partnerId"
                      className="form-select"
                      value={invoiceData.partnerId}
                      onChange={(e) =>
                        setInvoiceData({ ...invoiceData, partnerId: e.target.value })
                      }
                      required
                    >
                      <option value="">Select Partner</option>
                      {partners.map((partner) => (
                        <option key={partner._id} value={partner._id}>
                          {partner.name}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
              ) : (
                <div>
                  <h3>Invoice Items</h3>
                  <div className="mb-3">
                    <label htmlFor="productId" className="form-label">Product</label>
                    <select
                      id="productId"
                      name="productId"
                      className="form-select"
                      value={productId}
                      onChange={(e) => setProductId(e.target.value)}
                    >
                      <option value="">Select Product</option>
                      {products.map((product) => (
                        <option key={product._id} value={product._id}>
                          {product.name}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div className="mb-3">
                    <label htmlFor="quantity" className="form-label">Quantity</label>
                    <input
                      type="number"
                      id="quantity"
                      name="quantity"
                      className="form-control"
                      value={quantity}
                      onChange={(e) => setQuantity(e.target.value)}
                      min="1"
                      required
                    />
                  </div>
                </div>
              )}
              <div className="d-flex justify-content-between">
                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={handleNavigation}
                >
                  {page === 1 ? "Next" : "Back"}
                </button>
                {page === 2 && (
                  <>
                    <button type="submit" className="btn btn-success">
                      Submit
                    </button>
                    <button
                      type="button"
                      className="btn btn-danger"
                      onClick={handleCancel}
                    >
                      Cancel
                    </button>
                  </>
                )}
              </div>
            </form>
            {alertMessage && <Alert type={alertType} message={alertMessage} />}
          </>
        )}
      </div>
    </div>
  );
};

export default AddInvoice;
