import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Alert from "./Alert";
import Spinner from "./Spinner";

const Invoices = ({ invoiceType }) => {
  const [invoices, setInvoices] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [alert, setAlert] = useState(null);
  const navigate = useNavigate();

  // Fetch invoices based on invoiceType
  useEffect(() => {
    const fetchInvoices = async () => {
      try {
        const response = await axios.get(`/invoices?type=${invoiceType}`);
        if (response.status === 200) {
          setInvoices(response.data);
        } else {
          setAlert({ type: "danger", message: "Failed to fetch invoices." });
        }
        setIsLoading(false);
      } catch (error) {
        setAlert({
          type: "danger",
          message: "An error occurred while fetching invoices.",
        });
        setIsLoading(false);
      }
    };

    fetchInvoices();
  }, [invoiceType]);

  // Navigate to Invoice Report page
  const handleViewInvoice = (invoiceId) => {
    navigate(`/invoice-report?id=${invoiceId}`);
  };

  // Navigate to Add Invoice page
  const handleAddInvoice = () => {
    navigate(`/add-invoice?type=${invoiceType}`);
  };

  return (
    <div className="container mt-5">
      {alert && <Alert type={alert.type} message={alert.message} />}
      <div className="card shadow-lg border-0">
        <div className="card-header bg-gradient-primary text-white d-flex justify-content-between align-items-center">
          <h3 className="mb-0">
            {invoiceType === "sales" ? "Sales Invoices" : "Purchase Invoices"}
          </h3>
          <button className="btn btn-success" onClick={handleAddInvoice}>
            <i className="fa fa-plus"></i> Add Invoice
          </button>
        </div>
        <div className="card-body">
          {isLoading ? (
            <Spinner />
          ) : invoices.length > 0 ? (
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead style={{ backgroundColor: "#f2f7fc", color: "#000" }}>
                  <tr>
                    <th>Invoice Date</th>
                    <th>Invoice No</th>
                    <th>Due Date</th>
                    <th>{invoiceType === "sales" ? "Customer Name" : "Vendor Name"}</th>
                    <th>Total Items</th>
                    <th>Invoice Total</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {invoices.map((invoice) => (
                    <tr key={invoice._id}>
                      <td>{invoice.invoiceDate}</td>
                      <td>{invoice.invoiceNo}</td>
                      <td>{invoice.dueDate}</td>
                      <td>{invoice.partnerName}</td>
                      <td>{invoice.totalItems}</td>
                      <td>{invoice.invoiceTotal.toFixed(2)}</td>
                      <td>
                        <button
                          className="btn btn-sm btn-outline-primary"
                          onClick={() => handleViewInvoice(invoice._id)}
                        >
                          <i className="fa fa-eye"></i> View
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-center">No invoices found.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Invoices;
