import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Alert from "./Alert";

const AddPartner = () => {
  const navigate = useNavigate();

  const [alertType, setAlertType] = useState(null);
  const [alertMessage, setAlertMessage] = useState("");

  const initialState = {
    firstName: "",
    lastName: "",
    email: "",
    contactNo: "",
    status: "",
    address: "",
    openingBalance: "",
    repName: "",
    repContact: "",
    repDesignation: "",
  };
  const [partnerData, setPartnerData] = useState(initialState);
  const {
    firstName,
    lastName,
    email,
    contactNo,
    status,
    address,
    openingBalance,
    repName,
    repContact,
    repDesignation,
  } = partnerData;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPartnerData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/partners", partnerData);
      if (response.status === 201) {
        setAlertType("success");
        setAlertMessage(response.data);
        setPartnerData(initialState);
      } else {
        setAlertType("danger");
        setAlertMessage(
          `Failed to add partner: ${response.status} ${response.statusText}`
        );
      }
    } catch (error) {
      setAlertType("danger");
      setAlertMessage(`Failed to add partner: ${error.message}`);
    }
  };

  const handleCancel = () => {
    // Redirect to the partners page on cancel
    navigate("/partners");
  };

  return (
    <form onSubmit={handleSubmit} className="mt-4">
      <div className="row">
        <div className="col-md-6">
          <div className="mb-3">
            <label htmlFor="firstName" className="form-label">
              First Name
            </label>
            <input
              type="text"
              className="form-control"
              id="firstName"
              name="firstName"
              value={firstName}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Email
            </label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              value={email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="status" className="form-label">
              Status
            </label>
            <select
              className="form-select"
              id="status"
              name="status"
              value={status}
              onChange={handleChange}
              required
            >
              <option value="">Select Status</option>
              <option value="Active">Active</option>
              <option value="Inactive">Inactive</option>
            </select>
          </div>
          <div className="mb-3">
            <label htmlFor="address" className="form-label">
              Address
            </label>
            <textarea
              className="form-control"
              id="address"
              name="address"
              value={address}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="col-md-6">
          <div className="mb-3">
            <label htmlFor="lastName" className="form-label">
              Last Name
            </label>
            <input
              type="text"
              className="form-control"
              id="lastName"
              name="lastName"
              value={lastName}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="contactNo" className="form-label">
              Contact No.
            </label>
            <input
              type="text"
              className="form-control"
              id="contactNo"
              name="contactNo"
              value={contactNo}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="openingBalance" className="form-label">
              Opening Balance
            </label>
            <input
              type="number"
              className="form-control"
              id="openingBalance"
              name="openingBalance"
              value={openingBalance}
              onChange={handleChange}
              required
            />
          </div>
        </div>
      </div>
      <h3>Representative Information</h3>
      <div className="row mt-4">
        <div className="col-md-6">
          <div className="mb-3">
            <label htmlFor="repName" className="form-label">
              Full Name
            </label>
            <input
              type="text"
              className="form-control"
              id="repName"
              name="repName"
              value={repName}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="repContact" className="form-label">
              Contact No.
            </label>
            <input
              type="text"
              className="form-control"
              id="repContact"
              name="repContact"
              value={repContact}
              onChange={handleChange}
              required
            />
          </div>
        </div>
        <div className="col-md-6">
          <div className="mb-3">
            <label htmlFor="repDesignation" className="form-label">
              Designation
            </label>
            <input
              type="text"
              className="form-control"
              id="repDesignation"
              name="repDesignation"
              value={repDesignation}
              onChange={handleChange}
              required
            />
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-md-12">
          <button type="submit" className="btn btn-outline-success me-2">
            <i className="bi bi-save me-2"></i>Add
          </button>
          <button
            type="button"
            className="btn btn-outline-danger"
            onClick={handleCancel}
          >
            <i className="bi bi-x-square me-2"></i>Cancel
          </button>
        </div>
      </div>
      {alertMessage && <Alert type={alertType} message={alertMessage} />}
    </form>
  );
};

export default AddPartner;
