import React, { useEffect, useState } from "react";
import axios from "axios";
import NavBar from "./NavBar";
import Graph from "./Graph";
import Spinner from "./Spinner";
import Alert from "./Alert";

const Home = () => {
  const [groupedInvoices, setGroupedInvoices] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [alert, setAlert] = useState(null);

  // جلب بيانات الفواتير من السيرفر عند تحميل الصفحة
  useEffect(() => {
    const fetchInvoices = async () => {
      try {
        const response = await axios.get("/invoices");
        if (response.status === 200) {
          processInvoices(response.data); // معالجة البيانات بعد جلبها
        } else {
          setAlert({
            type: "danger",
            message: "Failed to fetch invoice data.",
          });
        }
      } catch (error) {
        setAlert({
          type: "danger",
          message: `Error: ${error.message}`,
        });
      } finally {
        setIsLoading(false); // إيقاف تحميل الـ Spinner بعد جلب البيانات
      }
    };

    fetchInvoices();
  }, []);

  // معالجة الفواتير وتجميعها حسب الأشهر الستة الأخيرة
  const processInvoices = (data) => {
    // إنشاء آخر 6 أشهر بتنسيق YYYY-MM
    const months = Array.from({ length: 6 }, (_, i) => {
      const date = new Date();
      date.setMonth(date.getMonth() - i);
      return date.toISOString().slice(0, 7); // تنسيق YYYY-MM
    }).reverse();

    // حساب إجمالي المبيعات والمشتريات لكل شهر
    const groupedData = months.map((month) => {
      const sales = data
        .filter(
          (invoice) =>
            invoice.type === "sales" && invoice.invoiceDate.startsWith(month)
        )
        .reduce((total, invoice) => total + parseFloat(invoice.invoiceTotal), 0);

      const purchases = data
        .filter(
          (invoice) =>
            invoice.type === "purchase" && invoice.invoiceDate.startsWith(month)
        )
        .reduce((total, invoice) => total + parseFloat(invoice.invoiceTotal), 0);

      return { month, sales, purchases };
    });

    setGroupedInvoices(groupedData); // تخزين البيانات في حالة
  };

  return (
    <div>
      <NavBar />
      <div className="container text-center mt-4">
        <h3>Sales and Purchase Overview (Last 6 Months)</h3>
        {alert && <Alert type={alert.type} message={alert.message} />}
        {isLoading ? (
          <Spinner />
        ) : groupedInvoices.length > 0 ? (
          <Graph graphData={groupedInvoices} />
        ) : (
          <p>No data available to display.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
