import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Alert from "./Alert";

const AddPartner = () => {
  const navigate = useNavigate();

  const [alert, setAlert] = useState({ type: null, message: "" });

  const initialFormState = {
    type: "customer",
    firstName: "",
    lastName: "",
    email: "",
    contactNo: "",
    status: "",
    address: "",
    openingBalance: "",
    representative: {
      name: "",
      contact: "",
      designation: "",
    },
  };

  const [formData, setFormData] = useState(initialFormState);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith("rep")) {
      setFormData((prev) => ({
        ...prev,
        representative: {
          ...prev.representative,
          [name.slice(3).toLowerCase()]: value,
        },
      }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
  
    // التحقق من الحقول الفارغة
    if (!formData.firstName || !formData.lastName || !formData.email || !formData.contactNo) {
      setAlert({
        type: "danger",
        message: "All fields are required. Please fill in all the fields.",
      });
      return;
    }
  
    // تجهيز البيانات قبل الإرسال
    const sanitizedData = {
      ...formData,
      openingBalance: parseFloat(formData.openingBalance) || 0,
      contactNo: parseInt(formData.contactNo, 10) || 0,
      representative: {
        ...formData.representative,
        contact: parseInt(formData.representative.contact, 10) || 0,
      },
    };
  
    try {
      const response = await axios.post("/partners", sanitizedData);
      if (response.status === 201) {
        setAlert({ type: "success", message: "Partner added successfully!" });
        setFormData(initialFormState); // إعادة تعيين النموذج
      } else {
        setAlert({
          type: "danger",
          message: `Failed to add partner: ${response.status} ${response.statusText}`,
        });
      }
    } catch (error) {
      setAlert({
        type: "danger",
        message: `Failed to add partner: ${
          error.response?.data?.message || error.message
        }`,
      });
    }
  };
  
  const handleCancel = () => {
    navigate("/partners");
  };

  return (
    <form onSubmit={handleFormSubmit} className="mt-4">
      <div className="row">
        <div className="col-md-6">
          <InputField
            label="First Name"
            id="firstName"
            name="firstName"
            value={formData.firstName}
            onChange={handleInputChange}
          />
          <InputField
            label="Email"
            id="email"
            name="email"
            value={formData.email}
            type="email"
            onChange={handleInputChange}
          />
          <SelectField
            label="Status"
            id="status"
            name="status"
            value={formData.status}
            options={["", "Active", "Inactive"]}
            onChange={handleInputChange}
          />
          <TextAreaField
            label="Address"
            id="address"
            name="address"
            value={formData.address}
            onChange={handleInputChange}
          />
        </div>
        <div className="col-md-6">
          <InputField
            label="Last Name"
            id="lastName"
            name="lastName"
            value={formData.lastName}
            onChange={handleInputChange}
          />
          <InputField
            label="Contact No."
            id="contactNo"
            name="contactNo"
            value={formData.contactNo}
            onChange={handleInputChange}
          />
          <InputField
            label="Opening Balance"
            id="openingBalance"
            name="openingBalance"
            type="number"
            value={formData.openingBalance}
            onChange={handleInputChange}
          />
        </div>
      </div>
      <h3>Representative Information</h3>
      <div className="row mt-4">
        <div className="col-md-6">
          <InputField
            label="Full Name"
            id="repName"
            name="repName"
            value={formData.representative.name}
            onChange={handleInputChange}
          />
          <InputField
            label="Contact No."
            id="repContact"
            name="repContact"
            value={formData.representative.contact}
            onChange={handleInputChange}
          />
        </div>
        <div className="col-md-6">
          <InputField
            label="Designation"
            id="repDesignation"
            name="repDesignation"
            value={formData.representative.designation}
            onChange={handleInputChange}
          />
        </div>
      </div>
      <div className="row">
        <div className="col-md-12">
          <button type="submit" className="btn btn-outline-success me-2">
            <i className="bi bi-save me-2"></i>Add
          </button>
          <button
            type="button"
            className="btn btn-outline-danger"
            onClick={handleCancel}
          >
            <i className="bi bi-x-square me-2"></i>Cancel
          </button>
        </div>
      </div>
      {alert.message && <Alert type={alert.type} message={alert.message} />}
    </form>
  );
};

const InputField = ({ label, id, name, value, onChange, type = "text" }) => (
  <div className="mb-3">
    <label htmlFor={id} className="form-label">
      {label}
    </label>
    <input
      type={type}
      className="form-control"
      id={id}
      name={name}
      value={value}
      onChange={onChange}
      required
    />
  </div>
);

const SelectField = ({ label, id, name, value, options, onChange }) => (
  <div className="mb-3">
    <label htmlFor={id} className="form-label">
      {label}
    </label>
    <select
      className="form-select"
      id={id}
      name={name}
      value={value}
      onChange={onChange}
      required
    >
      {options.map((option, index) => (
        <option key={index} value={option}>
          {option || "Select"}
        </option>
      ))}
    </select>
  </div>
);

const TextAreaField = ({ label, id, name, value, onChange }) => (
  <div className="mb-3">
    <label htmlFor={id} className="form-label">
      {label}
    </label>
    <textarea
      className="form-control"
      id={id}
      name={name}
      value={value}
      onChange={onChange}
    />
  </div>
);

export default AddPartner;
