import React, { useState, useEffect } from "react";
import NavBar from "./NavBar";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Spinner from "./Spinner";
import Alert from "./Alert";

const AddInvoice = ({ invoiceType }) => {
  const initialState = {
    type: invoiceType,
    invoiceDate: new Date().toISOString().split("T")[0],
    dueDate: new Date().toISOString().split("T")[0],
    invoiceNo: "",
    partnerId: "",
    creditTerm: 0,
    reference: "",
    invoiceTotal: 0,
    invoiceItems: [],
  };

  const [invoiceData, setInvoiceData] = useState(initialState);
  const [partners, setPartners] = useState([]);
  const [products, setProducts] = useState([]);
  const [productId, setProductId] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [isLoading, setIsLoading] = useState(true);
  const [alert, setAlert] = useState({ type: null, message: "" });

  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const partnerEndpoint =
          invoiceType === "sales" ? "/partners?type=customer" : "/partners?type=vendor";
        const invoiceEndpoint = `/invoices?type=${invoiceType}`;
        const productEndpoint = "/products";

        const [partnersRes, invoicesRes, productsRes] = await Promise.all([
          axios.get(partnerEndpoint),
          axios.get(invoiceEndpoint),
          axios.get(productEndpoint),
        ]);

        setPartners(partnersRes.data);
        setProducts(productsRes.data);

        const invoiceCount = invoicesRes.data.length;
        const newInvoiceNo =
          invoiceType === "sales"
            ? `SI-${String(invoiceCount + 1).padStart(4, "0")}`
            : `PI-${String(invoiceCount + 1).padStart(4, "0")}`;

        setInvoiceData((prevState) => ({ ...prevState, invoiceNo: newInvoiceNo }));
        setIsLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setAlert({ type: "danger", message: `Error fetching data: ${error.message}` });
        setIsLoading(false);
      }
    };

    fetchData();
  }, [invoiceType]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoiceData((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleCreditTermChange = (e) => {
    const term = parseInt(e.target.value, 10) || 0;
    const newDueDate = new Date();
    newDueDate.setDate(newDueDate.getDate() + term);
    setInvoiceData((prevState) => ({
      ...prevState,
      creditTerm: term,
      dueDate: newDueDate.toISOString().split("T")[0],
    }));
  };

  const addProduct = () => {
    const selectedProduct = products.find((p) => p._id === productId);
    if (!selectedProduct) {
      setAlert({ type: "warning", message: "Please select a valid product." });
      return;
    }

    const rate = parseFloat(selectedProduct.rate) || 0;
    const quantityInt = parseInt(quantity, 10) || 0;

    setInvoiceData((prevState) => ({
      ...prevState,
      invoiceItems: [
        ...prevState.invoiceItems,
        {
          productId,
          productName: selectedProduct.name,
          quantity: quantityInt,
          rate,
        },
      ],
      invoiceTotal: prevState.invoiceTotal + rate * quantityInt,
    }));

    setProductId("");
    setQuantity(1);
  };

  const handleRemoveInvoiceItem = (index) => {
    const items = [...invoiceData.invoiceItems];
    const removedItem = items.splice(index, 1);
    setInvoiceData((prevState) => ({
      ...prevState,
      invoiceItems: items,
      invoiceTotal: prevState.invoiceTotal - (removedItem[0]?.rate * removedItem[0]?.quantity || 0),
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!invoiceData.partnerId) {
      setAlert({ type: "warning", message: "Please select a partner." });
      return;
    }

    if (invoiceData.invoiceItems.length === 0) {
      setAlert({ type: "warning", message: "Please add at least one product." });
      return;
    }

    try {
      await axios.post("/invoices", invoiceData);
      setAlert({ type: "success", message: "Invoice added successfully!" });
      navigate(`/${invoiceType === "sales" ? "salesinvoices" : "purchaseinvoices"}`);
    } catch (error) {
      console.error("Error creating invoice:", error);
      setAlert({ type: "danger", message: "Failed to add invoice. Please try again." });
    }
  };

  const handleCancel = () => {
    navigate(`/${invoiceType === "sales" ? "salesinvoices" : "purchaseinvoices"}`);
  };

  return isLoading ? (
    <Spinner />
  ) : (
    <div className="container mt-5">
      {alert.message && <Alert type={alert.type} message={alert.message} className="mb-4" />}
      <div className="card shadow-lg border-0">
        <div className="card-header bg-gradient-primary text-white">
          <h3 className="text-center mb-0">Add {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice</h3>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="accordion" id="invoiceFormAccordion">
              {/* Basic Information Section */}
              <div className="accordion-item">
                <h2 className="accordion-header" id="invoiceBasicInfoHeading">
                  <button
                    className="accordion-button"
                    type="button"
                    data-bs-toggle="collapse"
                    data-bs-target="#basicInfo"
                    aria-expanded="true"
                    aria-controls="basicInfo"
                  >
                    Basic Information
                  </button>
                </h2>
                <div
                  id="basicInfo"
                  className="accordion-collapse collapse show"
                  aria-labelledby="invoiceBasicInfoHeading"
                >
                  <div className="accordion-body">
                    <div className="mb-3">
                      <label htmlFor="invoiceNo">Invoice No</label>
                      <input className="form-control" value={invoiceData.invoiceNo} disabled />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="partnerId">Partner</label>
                      <select
                        className="form-control"
                        id="partnerId"
                        name="partnerId"
                        value={invoiceData.partnerId}
                        onChange={handleChange}
                      >
                        <option value="">Select Partner</option>
                        {partners.map((partner) => (
                          <option key={partner._id} value={partner._id}>
                            {partner.firstName} {partner.lastName}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div className="mb-3">
                      <label htmlFor="reference">Reference</label>
                      <input
                        className="form-control"
                        id="reference"
                        name="reference"
                        value={invoiceData.reference}
                        onChange={handleChange}
                      />
                    </div>
                  </div>
                </div>
              </div>

              {/* Additional Information Section */}
              <div className="accordion-item">
                <h2 className="accordion-header" id="additionalInfoHeading">
                  <button
                    className="accordion-button collapsed"
                    type="button"
                    data-bs-toggle="collapse"
                    data-bs-target="#additionalInfo"
                    aria-expanded="false"
                    aria-controls="additionalInfo"
                  >
                    Additional Information
                  </button>
                </h2>
                <div
                  id="additionalInfo"
                  className="accordion-collapse collapse"
                  aria-labelledby="additionalInfoHeading"
                >
                  <div className="accordion-body">
                    {/* Add your fields for additional information */}
                  </div>
                </div>
              </div>
            </div>

            <div className="text-center mt-4">
              <button type="submit" className="btn btn-success me-3">
                <i className="fa fa-check"></i> Submit
              </button>
              <button type="button" className="btn btn-danger" onClick={handleCancel}>
                <i className="fa fa-times"></i> Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddInvoice;
