const navigate = useNavigate();

// State variables to manage the alert
const [alertType, setAlertType] = useState(null);
const [alertMessage, setAlertMessage] = useState("");

const handleChange = (e) => {
  const { name, value } = e.target;
  setPartnerData((prevData) => ({ ...prevData, [name]: value }));
};

const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    const response = await axios.post("/partners", partnerData);
    if (response.status === 201) {
      setAlertType("success");
      setAlertMessage(response.data);
      setPartnerData(initialState);
    } else {
      setAlertType("danger");
      setAlertMessage(
        `Failed to add ${partnerType}: ${response.status} ${response.statusText}`
      );
    }
  } catch (error) {
    setAlertType("danger");
    setAlertMessage(`Failed to add ${partnerType}: ${error.message}`);
  }
};

const handleCancel = () => {
  // Redirect to the customers/vendors page on cancel
  navigate(`/${partnerType}s`);
};
