import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Alert from "./Alert"; // Assuming this is where the Alert component is located.

const AddPartner = () => {
  const initialState = {
    type: "customer", // Can be "vendor" if toggled later
    firstName: "",
    lastName: "",
    email: "",
    contactNo: "",
    status: "",
    address: "",
    openingBalance: "",
    repName: "",
    repContact: "",
    repDesignation: "",
  };

  const [partnerData, setPartnerData] = useState(initialState);
  const [alert, setAlert] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPartnerData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("/partners", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(partnerData),
      });

      if (response.ok) {
        setAlert({ type: "success", message: "Partner added successfully!" });
        setPartnerData(initialState); // Reset form
      } else {
        const errorData = await response.json();
        setAlert({ type: "error", message: errorData.message || "Failed to add partner." });
      }
    } catch (error) {
      setAlert({ type: "error", message: "An error occurred while adding the partner." });
    }
  };

  const handleCancel = () => {
    navigate("/partners"); // Redirect to the partners page
  };

  return (
    <div className="container mt-5">
      {alert && (
        <Alert
          type={alert.type}
          message={alert.message}
          className="mb-4"
        />
      )}
      <div className="card shadow-lg">
        <div className="card-header bg-primary text-white">
          <h3 className="text-center mb-0">Add New Partner</h3>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label htmlFor="firstName" className="form-label">
                  First Name
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="firstName"
                  name="firstName"
                  value={partnerData.firstName}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6 mb-3">
                <label htmlFor="lastName" className="form-label">
                  Last Name
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="lastName"
                  name="lastName"
                  value={partnerData.lastName}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label htmlFor="email" className="form-label">
                  Email
                </label>
                <input
                  type="email"
                  className="form-control"
                  id="email"
                  name="email"
                  value={partnerData.email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6 mb-3">
                <label htmlFor="contactNo" className="form-label">
                  Contact Number
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="contactNo"
                  name="contactNo"
                  value={partnerData.contactNo}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="mb-3">
              <label htmlFor="status" className="form-label">
                Status
              </label>
              <input
                type="text"
                className="form-control"
                id="status"
                name="status"
                value={partnerData.status}
                onChange={handleChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="openingBalance" className="form-label">
                Opening Balance
              </label>
              <input
                type="number"
                className="form-control"
                id="openingBalance"
                name="openingBalance"
                value={partnerData.openingBalance}
                onChange={handleChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="address" className="form-label">
                Address
              </label>
              <textarea
                className="form-control"
                id="address"
                name="address"
                rows="3"
                value={partnerData.address}
                onChange={handleChange}
              ></textarea>
            </div>
            <div className="row">
              <div className="col-md-4 mb-3">
                <label htmlFor="repName" className="form-label">
                  Representative Name
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="repName"
                  name="repName"
                  value={partnerData.repName}
                  onChange={handleChange}
                />
              </div>
              <div className="col-md-4 mb-3">
                <label htmlFor="repContact" className="form-label">
                  Representative Contact
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="repContact"
                  name="repContact"
                  value={partnerData.repContact}
                  onChange={handleChange}
                />
              </div>
              <div className="col-md-4 mb-3">
                <label htmlFor="repDesignation" className="form-label">
                  Representative Designation
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="repDesignation"
                  name="repDesignation"
                  value={partnerData.repDesignation}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="text-center">
              <button type="submit" className="btn btn-success me-3">
                Submit
              </button>
              <button
                type="button"
                className="btn btn-danger"
                onClick={handleCancel}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddPartner;
