import React, { useState, useEffect } from "react";
import NavBar from "./NavBar";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Spinner from "./Spinner";
import Alert from "./Alert";

const AddInvoice = ({ invoiceType }) => {
  const initialState = {
    type: invoiceType,
    invoiceDate: new Date().toISOString().split("T")[0],
    dueDate: new Date().toISOString().split("T")[0],
    invoiceNo: "",
    partnerId: "",
    creditTerm: 0,
    reference: "",
    invoiceTotal: 0,
    invoiceItems: [],
  };

  const [invoiceData, setInvoiceData] = useState(initialState);
  const [partners, setPartners] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState(null);

  const navigate = useNavigate();

  const fetchInitialData = async () => {
    setLoading(true);
    try {
      const partnerType = invoiceType === "sales" ? "customers" : "vendors";
      const partnersResponse = await axios.get(`/partners?type=${partnerType}`);
      if (partnersResponse.status === 200) {
        setPartners(partnersResponse.data); // تعيين الشركاء
      } else {
        console.error("Failed to fetch partners:", partnersResponse.statusText);
      }
  
      const productsResponse = await axios.get(`/products`);
      if (productsResponse.status === 200) {
        setProducts(productsResponse.data); // تعيين المنتجات
      } else {
        console.error("Failed to fetch products:", productsResponse.statusText);
      }
  
      const invoicesResponse = await axios.get(`/invoices?type=${invoiceType}`);
      if (invoicesResponse.status === 200) {
        const invoiceCount = invoicesResponse.data.length + 1;
        const prefix = invoiceType === "sales" ? "SI-" : "PI-";
        const invoiceNo = `${prefix}${String(invoiceCount).padStart(4, "0")}`;
        setInvoiceData((prev) => ({ ...prev, invoiceNo }));
      }
    } catch (error) {
      console.error("Error fetching data:", error);
      setAlert({ type: "danger", message: "Error fetching data." });
    } finally {
      setLoading(false);
    }
  };
  
  useEffect(() => {
    fetchInitialData();
  }, [invoiceType]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoiceData((prev) => ({ ...prev, [name]: value }));
  };

  const handleCreditTermChange = (e) => {
    const creditTerm = parseInt(e.target.value, 10) || 0;
    const dueDate = new Date();
    dueDate.setDate(dueDate.getDate() + creditTerm);
    setInvoiceData((prev) => ({
      ...prev,
      creditTerm,
      dueDate: dueDate.toISOString().split("T")[0],
    }));
  };

  const addProduct = (productId, quantity) => {
    const product = products.find((p) => p.id === productId);
    if (!product) {
      setAlert({ type: "warning", message: "Product not found!" });
      return;
    }

    const valueOfSupplies = quantity * product.rate;
    const salesTax = (valueOfSupplies * product.salesTax) / 100;
    const netAmount = valueOfSupplies + salesTax;

    const newItem = {
      productId,
      quantity,
      valueOfSupplies,
      salesTax,
      netAmount,
    };

    setInvoiceData((prev) => ({
      ...prev,
      invoiceItems: [...prev.invoiceItems, newItem],
      invoiceTotal: prev.invoiceTotal + netAmount,
    }));
  };

  const handleRemoveInvoiceItem = (index) => {
    const items = [...invoiceData.invoiceItems];
    const removedItem = items.splice(index, 1);
    setInvoiceData((prev) => ({
      ...prev,
      invoiceItems: items,
      invoiceTotal: prev.invoiceTotal - removedItem[0].netAmount,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post("/invoices", invoiceData);
      setAlert({ type: "success", message: "Invoice created successfully!" });
      navigate(`/invoices/${invoiceType}`);
    } catch (error) {
      setAlert({ type: "danger", message: "Error creating invoice." });
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    navigate(`/invoices/${invoiceType}`);
  };

  return (
    <div>
      <NavBar />
      <div className="container mt-4">
        {loading && <Spinner />}
        {alert && <Alert type={alert.type} message={alert.message} />}
        <form onSubmit={handleSubmit}>
          <h3>Add {invoiceType === "sales" ? "Sales" : "Purchase"} Invoice</h3>

          <div className="form-group">
            <label>Invoice No</label>
            <input
              type="text"
              className="form-control"
              value={invoiceData.invoiceNo}
              readOnly
            />
          </div>

          <div className="form-group">
            <label>Partner</label>
            <select
              name="partnerId"
              className="form-control"
              value={invoiceData.partnerId}
              onChange={handleChange}
            >
              <option value="">Select Partner</option>
              {partners.map((partner) => (
                <option key={partner.id} value={partner.id}>
                  {partner.name}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Reference</label>
            <input
              type="text"
              name="reference"
              className="form-control"
              value={invoiceData.reference}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>Due Date</label>
            <input
              type="date"
              className="form-control"
              name="dueDate"
              value={invoiceData.dueDate}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>Credit Term</label>
            <input
              type="number"
              className="form-control"
              name="creditTerm"
              value={invoiceData.creditTerm}
              onChange={handleCreditTermChange}
            />
          </div>

          <div className="mt-4">
            <h5>Invoice Items</h5>
            <div className="form-row">
              <div className="col">
                <select className="form-control">
                  <option>Select Product</option>
                  {/* Add product options dynamically */}
                </select>
              </div>
              <div className="col">
                <input type="number" className="form-control" placeholder="Quantity" />
              </div>
              <div className="col">
                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={() => addProduct(1, 10)} // Update as necessary
                >
                  Add
                </button>
              </div>
            </div>
          </div>

          <table className="table mt-3">
            <thead>
              <tr>
                <th>Product</th>
                <th>Quantity</th>
                <th>Value</th>
                <th>Tax</th>
                <th>Net</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {invoiceData.invoiceItems.map((item, index) => (
                <tr key={index}>
                  <td>{item.productId}</td>
                  <td>{item.quantity}</td>
                  <td>{item.valueOfSupplies}</td>
                  <td>{item.salesTax}</td>
                  <td>{item.netAmount}</td>
                  <td>
                    <button
                      type="button"
                      className="btn btn-danger"
                      onClick={() => handleRemoveInvoiceItem(index)}
                    >
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <button type="submit" className="btn btn-success">
            Submit
          </button>
          <button type="button" className="btn btn-secondary ml-2" onClick={handleCancel}>
            Cancel
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddInvoice;
