import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import Alert from "./Alert";
import Spinner from "./Spinner";

const EditPartner = ({ partnerType }) => {
  const location = useLocation();
  const navigate = useNavigate();

  const [partnerData, setPartnerData] = useState({});
  const [isLoading, setIsLoading] = useState(true); 

  const [alertType, setAlertType] = useState(null);
  const [alertMessage, setAlertMessage] = useState("");

  useEffect(() => {
    const fetchPartner = async () => {
      try {
        // Get the partner ID from URL parameters
        const queryParams = new URLSearchParams(location.search);
        const id = queryParams.get("id");

        // Fetch partner data using the partner ID
        const response = await axios.get(`/partners?id=${id}`);

        if (response.status === 200) {
          // If successful, update the partner state with the response data
          setPartnerData(response.data);
        } else if (response.status === 404) {
          // Handle a 404 Not Found response
          setAlertType("danger");
          setAlertMessage(
            `${partnerType === "customer" ? "Customer" : "Vendor"} not found.`
          );
        } else {
          // Handle other error responses
          setAlertType("danger");
          setAlertMessage(
            `Failed to retrieve ${partnerType}: ${response.status} ${response.statusText}`
          );
        }
        setIsLoading(false);
      } catch (error) {
        setAlertType("danger");
        setAlertMessage(`Failed to retrieve ${partnerType}: ${error.message}`);
        setIsLoading(false); // Set loading state to false if there's an error
      }
    };

    fetchPartner();
  }, [location.search, partnerType]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPartnerData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.put(
        `/partners/${partnerData._id}`,
        partnerData
      );

      if (response.status === 200) {
        // Redirect to the customers/vendors page after successful update
        navigate(`/${partnerType}s`);
      } else if (response.status === 404) {
        setAlertType("danger");
        setAlertMessage(
          partnerType === "customer"
            ? "Customer not found."
            : "Vendor not found."
        );
      } else {
        setAlertType("danger");
        setAlertMessage(
          `Failed to update ${partnerType}: ${response.status} ${response.statusText}`
        );
      }
    } catch (error) {
      setAlertType("danger");
      setAlertMessage(`Failed to update ${partnerType}: ${error.message}`);
    }
  };

  const handleCancel = () => {
    // Redirect to the customers/vendors page on cancel
    navigate(`/${partnerType}s`);
  };

  return (
    <div>
      {isLoading ? (
        <Spinner />
      ) : (
        <>
          <h2>Edit {partnerType === "customer" ? "Customer" : "Vendor"}</h2>
          <form onSubmit={handleSubmit} className="mt-4">
            <div className="row">
              <div className="col-md-6">
                <div className="mb-3">
                  <label htmlFor="firstName" className="form-label">
                    First Name
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="firstName"
                    name="firstName"
                    value={partnerData.firstName || ""}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="email" className="form-label">
                    Email
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="email"
                    name="email"
                    value={partnerData.email || ""}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="status" className="form-label">
                    Status
                  </label>
                  <select
                    className="form-select"
                    id="status"
                    name="status"
                    value={partnerData.status || ""}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Status</option>
                    <option value="Active">Active</option>
                    <option value="Inactive">Inactive</option>
                  </select>
                </div>
                <div className="mb-3">
                  <label htmlFor="address" className="form-label">
                    Address
                  </label>
                  <textarea
                    className="form-control"
                    id="address"
                    name="address"
                    value={partnerData.address || ""}
                    onChange={handleChange}
                  />
                </div>
              </div>
              <div className="col-md-6">
                <div className="mb-3">
                  <label htmlFor="lastName" className="form-label">
                    Last Name
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="lastName"
                    name="lastName"
                    value={partnerData.lastName || ""}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="contactNo" className="form-label">
                    Contact No.
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="contactNo"
                    name="contactNo"
                    value={partnerData.contactNo || ""}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="openingBalance" className="form-label">
                    Opening Balance
                  </label>
                  <input
                    type="number"
                    className="form-control"
                    id="openingBalance"
                    name="openingBalance"
                    value={partnerData.openingBalance || ""}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
            </div>
            <h3>Representative Information</h3>
            <div className="row mt-4">
              <div className="col-md-6">
                <div className="mb-3">
                  <label htmlFor="repName" className="form-label">
                    Full Name
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="repName"
                    name="repName"
                    value={partnerData.repName || ""}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="repContact" className="form-label">
                    Contact No.
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="repContact"
                    name="repContact"
                    value={partnerData.repContact || ""}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              <div className="col-md-6">
                <div className="mb-3">
                  <label htmlFor="repDesignation" className="form-label">
                    Designation
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="repDesignation"
                    name="repDesignation"
                    value={partnerData.repDesignation || ""}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col-md-12">
                <button
                  type="submit"
                  className="btn btn-outline-success me-2"
                >
                  <i className="bi bi-save me-2"></i>Update
                </button>
                <button
                  type="button"
                  className="btn btn-outline-danger"
                  onClick={handleCancel}
                >
                  <i className="bi bi-x-square me-2"></i>Cancel
                </button>
              </div>
            </div>
          </form>
          {alertMessage && <Alert type={alertType} message={alertMessage} />}
        </>
      )}
    </div>
  );
};

export default EditPartner;
